// Input: ?
=pv!deploymentUuid

// Output: ?
ac!Result.body.status

// Output: ?
/*The below match is to explicitly error the node on deployment failure*/
a!match(
  value: null,
  whenTrue: rule!CMGT_UTIL_IsNotBlank(input: ac!Error),
  then: error(
    joinarray(
      {
        ac!Error.title,
        ac!Error.message,
        ac!Error.detail
      },
      ","
    )
  ),
  whenTrue: rule!CMGT_UTIL_IsBlank(value: pv!deploymentUuid),
  then: error("Deployment UUID is null"),
  whenTrue: ac!Result.body.status = cons!CMGT_EXT_PROCESS_STATUS_FAILED,
  then: error(
    "Deployment failed with a status code:" & ac!Result.body.status
  ),
  default: ""
)

