_a-0000ec0f-47ad-8000-9ba8-011c48011c48_.sail_code:
a!localVariables(
  local!tasks: rule!CMGT_QR_GetTask(
    caseId: ri!caseId,
    fields: {
      recordType!CMGT_Task.comments,
      recordType!CMGT_Task.documents,
      recordType!CMGT_Task.comments,
      recordType!CMGT_Task.comments,
      recordType!CMGT_Task.taskResponseOptions
    },
    includeRoles: true(),
    includeCase: true()
  ),
  a!flatten(
    #"SYSTEM_SYSRULES_forEach"(
      items: local!tasks,
      expression: rule!CMGT_UTIL_RejectBlank(
        {
          /*Create Task*/
          rule!CMGT_TaskEventHistory_Constructor(
            taskId: fv!item[recordType!CMGT_Task.taskId],
            initiator: fv!item[recordType!CMGT_Task.createdBy],
            eventTypeId: cons!CMGT_REFID_EVENT_TYPE_TASK_CREATE,
            timestamp: fv!item[recordType!CMGT_Task.createdOn]
          ),
          /*Status Update*/
          if(
            or(fv!item[recordType!CMGT_Task.statusId] = cons!CMGT_REFID_STATUS_TASK_READY,
            fv!item[recordType!CMGT_Task.statusId] = cons!CMGT_REFID_STATUS_TASK_QUEUED),
            null(),
            rule!CMGT_TaskEventHistory_Constructor(
              taskId: fv!item[recordType!CMGT_Task.taskId],
              initiator: fv!item[recordType!CMGT_Task.modifiedBy],
              eventTypeId: if(
                fv!item[recordType!CMGT_Task.statusId] = cons!CMGT_REFID_STATUS_TASK_NOT_NEEDED,
                cons!CMGT_REFID_EVENT_TYPE_TASK_NOT_NEEDED,
                cons!CMGT_REFID_EVENT_TYPE_TASK_COMPLETE
              ),
              details: if(
                fv!item[recordType!CMGT_Task.statusId] = cons!CMGT_REFID_STATUS_TASK_NOT_NEEDED,
                null,
                a!match(
                  value: fv!item[recordType!CMGT_Task.taskBehaviorTypeId],
                  equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_DECISION,
                  then: a!map(
                    taskResponseOptionLabelKey: displayvalue(
                      fv!item[recordType!CMGT_Task.taskResponseOptionId],
                      fv!item[recordType!CMGT_Task.taskResponseOptions.taskResponseOptionId],
                      fv!item[recordType!CMGT_Task.taskResponseOptions.label],
                      null
                    )
                  ),
                  equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_DOCUMENT_UPLOAD,
                  then: a!map(
                    value: joinarray(
                      #"SYSTEM_SYSRULES_forEach"(
                        items: rule!CMGT_UTIL_Filter(
                          records: fv!item[recordType!CMGT_Task.documents],
                          field: recordType!CMGT_Document.createdOn,
                          value: fv!item[recordType!CMGT_Task.completedOn]
                        ),
                        expression: document(
                          documentId: fv!item[recordType!CMGT_Document.appianDocId],
                          property: "name"
                        ) & "." & lower(
                          document(
                            documentId: fv!item[recordType!CMGT_Document.appianDocId],
                            property: "extension"
                          )
                        )
                      ),
                      ","
                    )
                  ),
                  default: null
                )
              ),
              timestamp: fv!item[recordType!CMGT_Task.modifiedOn]
            )
          ),
          /*Roles  */
          if(
            rule!CMGT_UTIL_IsBlank(
              fv!item[recordType!CMGT_Task.roles]
            ),
            null,
            rule!CMGT_EXT_Role_AddEvent_Constructor(
              roles: rule!CMGT_UTIL_SetForeignKey(
                records: fv!item,
                relationship: recordType!CMGT_Task.roles,
                parentPkField: recordType!CMGT_Task.taskId,
                childFkField: recordType!CMGT_Role.taskId
              ),
              context: "TASK"
            )
          ),
          /*Comments*/
          if(
            rule!CMGT_UTIL_IsBlank(
              fv!item[recordType!CMGT_Task.comments]
            ),
            null,
            rule!CMGT_EXT_CommentEvent_Constructor(
              comments: rule!CMGT_UTIL_SetForeignKey(
                records: fv!item,
                relationship: recordType!CMGT_Task.comments,
                parentPkField: recordType!CMGT_Task.taskId,
                childFkField: recordType!CMGT_Comment.taskId
              ),
              context: "TASK"
            )
          ),
          /* - Documents*/
          if(
            rule!CMGT_UTIL_IsBlank(
              fv!item[recordType!CMGT_Task.documents]
            ),
            {},
            rule!CMGT_EXT_Document_SetEvents(
              documents: fv!item[recordType!CMGT_Task.documents],
              eventType: "CREATE",
              context: "TASK"
            )
          )
        }
      )
    )
  )
)

