_a-0000ea01-8773-8000-9bde-011c48011c48_.sail_code:
a!localVariables(
  local!maxPerRow: a!defaultValue(ri!maxPerRow, count(ri!cardLayouts)),
  #"SYSTEM_SYSRULES_forEach"(
    items: enumerate(
      ceiling(
        count(ri!cardLayouts) / local!maxPerRow,
        1
      )
    ),
    expression: a!localVariables(
      local!rowIndex: fv!index,
      #"SYSTEM_SYSRULES_columnsLayout"(
        marginBelow: ri!marginBelow,
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            showWhen: contains({ "RIGHT", "CENTER" }, ri!align)
          ),
          #"SYSTEM_SYSRULES_forEach"(
            items: enumerate(local!maxPerRow),
            expression: a!localVariables(
              local!adjustedIndex: (local!rowIndex - 1) * local!maxPerRow + fv!index,
              local!cardLayout: a!refreshVariable(
                value: index(ri!cardLayouts, local!adjustedIndex, {}),
                refreshAlways: true
              ),
              #"SYSTEM_SYSRULES_columnLayout"(
                width: ri!columnWidth,
                contents: local!cardLayout
              )
            )
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            showWhen: contains({ "LEFT", "CENTER" }, ri!align)
          )
        },
        stackWhen: ri!stackWhen,
        spacing: a!defaultValue(ri!spacing,"DENSE")
      )
    )
  )
)

_a-0000eaba-1e20-8000-9c2a-011c48011c48_.sail_code:
if(
  or(
    isnull(ri!colorSchemeValue),
    contains(rule!CMGT_ENUM_BRAND_STANDARD_COLOR_SCHEMES().COLOR_SCHEME.colorSchemes.key, ri!colorSchemeValue)
  ),

  /*Is a standard color scheme (e.g. MIDNIGHT)*/
  if(
    ri!returnRawHexCodes,
    a!flatten(displayvalue(
      ri!colorSchemeValue,
      rule!CMGT_ENUM_BRAND_STANDARD_COLOR_SCHEMES().COLOR_SCHEME.colorSchemes.key,
      rule!CMGT_ENUM_BRAND_STANDARD_COLOR_SCHEMES().COLOR_SCHEME.colorSchemes.hexCodes,
      {}
    )),
    ri!colorSchemeValue
  ),

  /*Is a custom color scheme*/
  if(
    ri!returnRawHexCodes,
    split(ri!colorSchemeValue, ","),
    #"SYSTEM_SYSRULES_colorSchemeCustom"(colors: split(ri!colorSchemeValue, ","))
  )
)

