a!localVariables(
  local!distinctValues: rule!CMGT_UTIL_Distinct(
    reject(a!isNullOrEmpty, a!flatten(ri!values))
  ),
  local!formatValues: #"SYSTEM_SYSRULES_forEach"(
    items: local!distinctValues,
    expression: if(
      runtimetypeof(fv!item) = 'type!{http://www.appian.com/ae/types/2009}Group',
      group(tointeger(fv!item), "groupName"),
      fv!item
    )
  ),
  local!formattedNamesBeforeOther: index(
    local!formatValues,
    enumerate(cons!CMGT_INT_BATCH_SIZE_EXTRA_SMALL - 1) + 1,
    null
  ),
  local!formattedNamesForCaption: difference(
    local!formatValues,
    local!formattedNamesBeforeOther
  ),
  local!count: a!defaultValue(
    ri!totalCount,
    count(local!distinctValues)
  ),
  local!separator: a!defaultValue(ri!separator, char(10)),
  /*Hide options > cons!CMGT_INT_NUM_OF_OBJECTS_TO_SHOW_BEFORE_HIDE,
          use the cons!CMGT_INT_NUM_OF_OBJECTS_TO_SHOW_BEFORE_HIDE object to be the "show more"
  */
  #"SYSTEM_SYSRULES_richTextDisplayField"(
    label: rule!CMGT_UTIL_ReturnLabelGivenAutomationTestingSwitch(testingLabel: "List"),
    labelPosition: "COLLAPSED",
    value: if(
      local!count < cons!CMGT_INT_BATCH_SIZE_EXTRA_SMALL,
      {
        if(
          a!isNotNullOrEmpty(value: ri!icon),
          {
            #"SYSTEM_SYSRULES_richTextIcon"(
              icon: ri!icon,
              caption: ri!iconCaption,
              color: a!defaultValue(
                ri!iconColor,
                ri!brandingMap.SecondaryTextColor
              ),
              size: "SMALL"
            ),
            char(32)
          },
          {}
        ),
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: if(
            a!isNullOrEmpty(local!formatValues),
            ri!emptyLabel,
            joinarray(local!formatValues, local!separator)
          ),
          color: a!defaultValue(
            if(
              a!isNullOrEmpty(local!formatValues),
              ri!brandingMap.SecondaryTextColor,
              ri!textColor
            ),
            ri!brandingMap.StandardTextColor
          ),
          size: a!defaultValue(ri!textSize, "STANDARD"),
          style: if(
            a!isNullOrEmpty(local!formatValues),
            "EMPHASIS",
            ri!textStyle
          )
        )
      },
      {
        if(
          a!isNotNullOrEmpty(value: ri!icon),
          {
            #"SYSTEM_SYSRULES_richTextIcon"(
              icon: ri!icon,
              caption: ri!iconCaption,
              color: a!defaultValue(
                ri!iconColor,
                ri!brandingMap.SecondaryTextColor
              ),
              size: "SMALL"
            ),
            char(32)
          },
          {}
        ),
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: {
            joinarray(
              local!formattedNamesBeforeOther,
              local!separator
            ),
            local!separator
          },
          color: a!defaultValue(
            if(
              a!isNullOrEmpty(local!distinctValues),
              ri!brandingMap.SecondaryTextColor,
              ri!textColor
            ),
            ri!brandingMap.StandardTextColor
          ),
          size: a!defaultValue(ri!textSize, "STANDARD"),
          style: if(
            a!isNullOrEmpty(local!distinctValues),
            "EMPHASIS",
            ri!textStyle
          )
        ),
        #"SYSTEM_SYSRULES_richTextIcon"(
          icon: "plus",
          caption: joinarray(local!formattedNamesForCaption, ", "),
          color: ri!brandingMap.SecondaryTextColor,
          size: "SMALL"
        ),
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: a!localVariables(
            local!countForMoreAvailable: (
              local!count - cons!CMGT_INT_BATCH_SIZE_EXTRA_SMALL
            ) + 1,
            #"urn:appian:translation-string:v1:138e2717-f77d-4cb5-9d38-6e79f12039c7"(#"urn:appian:translation-variable:v1:706bfb1d-813b-4e07-adb1-b21bb5d5018b": local!countForMoreAvailable)
          ),
          color: a!defaultValue(
            if(
              a!isNullOrEmpty(local!distinctValues),
              ri!brandingMap.SecondaryTextColor,
              ri!textColor
            ),
            ri!brandingMap.StandardTextColor
          ),
          size: a!defaultValue(ri!textSize, "STANDARD"),
          style: if(
            a!isNullOrEmpty(local!formatValues),
            "EMPHASIS",
            ri!textStyle
          )
        )
      }
    ),
    showWhen: or(
      a!isNotNullOrEmpty(local!distinctValues),
      a!isNotNullOrEmpty(ri!emptyLabel)
    ),
    align: "LEFT",
    marginAbove: a!defaultValue(
      value: ri!marginAbove,
