_a-0000e99c-1217-8000-9ba9-011c48011c48_.sail_code:
if(
  a!isNullOrEmpty(value: ri!dateTime),
  null(),
  local(
    gmt(ri!dateTime),
    a!defaultValue(
      value: ri!timezone,
      default: rule!CMGT_UTIL_SystemTimezone()
    )
  )
)

/*todate and totime function will return the value based on GMT timezone when date and time is passed as input to this functions
so in order to get the date or time in user's timezone/system time zone using below logic */
if(
  a!isNullOrEmpty(value: ri!dateTimeValue),
  null(),
  a!localVariables(
    /*Converting dateTime to system time zone if  convertToSystemTimeZone is passed true */
    local!dateTimeToFormat: if(
      ri!convertToSystemTimeZone,
      rule!CMGT_UTIL_ConvertDateTimeToTimezone(
        dateTime: ri!dateTimeValue,
        timezone: rule!CMGT_UTIL_SystemTimezone()
      ),
      ri!dateTimeValue
    ),
    a!match(
      value: tostring(userlocale(loggedInUser())),
      equals: "en_US",
      then: todate(
        text(local!dateTimeToFormat, "mm/dd/yyyy")
      ),
      equals: "ar",
      then: text(local!dateTimeToFormat, "yyyy/mm/dd"),
      default: todate(
        text(local!dateTimeToFormat, "dd-mm-yyyy")
      )
    )
  )
)

_a-0000e9fa-aa45-8000-9bda-011c48011c48_.sail_code:
#"SYSTEM_SYSRULES_forEach"(
  items: ri!user,
  expression: a!localVariables(
    local!currentUserRecord: if(
      isusernametaken(fv!item),
      rule!CMGT_UTIL_Filter(
        records: ri!userRecord,
        field: #"urn:appian:record-field:v1:SYSTEM_RECORD_TYPE_USER/SYSTEM_RECORD_TYPE_USER_FIELD_username",
        value: fv!item
      ),
      null()
    ),
    cast(
      'type!{http://www.appian.com/ae/types/2009}Text',
      if(
        rule!CMGT_UTIL_IsNotBlank(input: local!currentUserRecord),
        if(
          or(
            local!currentUserRecord[#"urn:appian:record-field:v1:SYSTEM_RECORD_TYPE_USER/SYSTEM_RECORD_TYPE_USER_FIELD_isServiceAccount"] = true,
            #"urn:appian:function:v1:a:isusermemberofgroup?okey=a!isUserMemberOfGroup"(
              username: fv!item,
              groups: cons!CMGT_GRP_SERVICE_ACCOUNTS
            )
          ),
          cons!CMGT_TXT_AUTOMATED_SYSTEM,
          local!currentUserRecord[#"urn:appian:record-field:v1:SYSTEM_RECORD_TYPE_USER/SYSTEM_RECORD_TYPE_USER_FIELD_firstAndLastName"]
        ),
        if(
          isnull(fv!item),
          if(
            ri!returnNullForEmpty,
            null,
            cons!CMGT_TXT_EMPTY_VALUE
          ),
          fv!item
        )
      )
    )
  )
)

