/*
Note that because a forEach that loops over a single item is significantly less performant than just calling the thing,
we only want to use forEach here if multiple keys are passed, since this rule is used on every text on every interface
(many calls per page load).

Rather than create a separate rule that just adds to rule sprawl, we just commented the common blocks of identical code.
Updates to either one should be reflected in the other.
*/
a!localVariables(
  /*Using this to avoid re-evaluation on page interaction*/
  local!output: cast(
    /*Always ensure the key is returned as the same list-type of the input, especially for null/empty keys*/
    runtimetypeof(ri!key),
    if(
      a!isNullOrEmpty(ri!key),
      {},
      if(
        count(a!flatten(ri!key)) = 1,
        a!localVariables(
          local!key: a!flatten(ri!key)[1],
          /*START OF COMMON CODE*/
          local!rawLabel:a!defaultValue(ri!brandingMap[local!key], local!key),
          /*END OF COMMON CODE*/
          local!rawLabel
        ),
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!key,
          expression: a!localVariables(
            local!key: fv!item,
            /*START OF COMMON CODE*/
            local!rawLabel: a!defaultValue(ri!brandingMap[local!key], local!key),

            /*END OF COMMON CODE*/
            local!rawLabel
          )
        )
      )
    )
  ),
  local!output
)

_a-0000ece2-0128-8000-9bb8-011c48011c48_.sail_code:
if(
  and(
    rule!CMGT_UTIL_IsNotBlank(ri!dueOn),
    ri!showAsOverdueWhen
  ),
  a!localVariables(
    local!elapsedDays: elapseddays(ri!dueOn, now()),
    local!elapsedHours: elapsedhours(ri!dueOn, now()),
    local!elapsedMinutes: elapsedminutes(ri!dueOn, now()),
    local!getDateFromDueDateConverted: rule!CMGT_UTIL_GetDateFromDateTime(
      dateTimeValue: ri!dueOn,
      convertToSystemTimeZone: false
    ),
    a!match(
      value: local!getDateFromDueDateConverted,
      /* Overdue by days     */
      whenTrue: local!elapsedDays > 0,
      then: a!map(
        icon: #"SYSTEM_SYSRULES_richTextIcon"(
          icon: "exclamation-circle",
          size: ri!iconSize,
          color: ri!brandingMap.NegativeAccentColor,
          caption: #"urn:appian:translation-string:v1:d7fef3a3-051d-443a-b953-149895f8a3c8"
        ),
        text: if(
          local!elapsedDays = 1,
          #"urn:appian:translation-string:v1:b179fba0-6749-4045-b6bf-8a18629bb0ad"(
            #"urn:appian:translation-variable:v1:40b4aa38-acbc-4588-b634-cd5ce89462d4": local!elapsedDays
          ),
          #"urn:appian:translation-string:v1:41a4b792-f85b-47bd-84cb-9969ff768b5b"(
            #"urn:appian:translation-variable:v1:ce229db1-aeef-4add-873e-17aa7a70938e": local!elapsedDays
          )
        )
      ),
      /*   Overdue by hours   */
      whenTrue: and(
        local!elapsedHours > 0,
        local!elapsedHours < 24
      ),
      then: a!map(
        icon: #"SYSTEM_SYSRULES_richTextIcon"(
          icon: "exclamation-circle",
          size: ri!iconSize,
          color: ri!brandingMap.NegativeAccentColor,
          caption: #"urn:appian:translation-string:v1:d7fef3a3-051d-443a-b953-149895f8a3c8"
        ),
        text: if(
          local!elapsedHours = 1,
          #"urn:appian:translation-string:v1:1a0d1c13-62a2-4920-82b8-c9e6c6b94abf"(
            #"urn:appian:translation-variable:v1:5e46f0b5-0f17-4e73-bd77-fe5cc91ebc24": local!elapsedHours
          ),
          #"urn:appian:translation-string:v1:283f2c46-04f4-4cfe-87f1-3948c1cd2684"(
            #"urn:appian:translation-variable:v1:c3e94e73-9d02-414a-aca6-6418b7a58222": local!elapsedHours
          )
        )
      ),
      /*  Overdue by minutes    */
      whenTrue: and(
        local!elapsedMinutes > 0,
        local!elapsedMinutes < 60
      ),
      then: a!map(
        icon: #"SYSTEM_SYSRULES_richTextIcon"(
          icon: "clock",
          size: ri!iconSize,
          color: ri!brandingMap.WarningAccentColor,
          caption: #"urn:appian:translation-string:v1:d7fef3a3-051d-443a-b953-149895f8a3c8"
        ),
        text: if(
          local!elapsedMinutes = 1,
          #"urn:appian:translation-string:v1:a5b4fb2f-5c76-4fce-b138-3c6bdc99a65b"(
            #"urn:appian:translation-variable:v1:4520261c-795e-4420-b8e1-1a7352034a43": local!elapsedMinutes
          ),
          #"urn:appian:translation-string:v1:73a34328-d743-4473-aa26-1798314ed42f"(
            #"urn:appian:translation-variable:v1:754b7c44-1511-44f8-ba35-bd8aa238d558": local!elapsedMinutes
          )
        )
      ),
      default: a!map(
        icon: #"SYSTEM_SYSRULES_richTextIcon"(
          icon: "calendar-o",
          size: ri!iconSize,
          color: ri!brandingMap.SecondaryTextColor
        ),
        text: rule!CMGT_UTIL_FormatDateTime(dateTime: ri!dueOn, includeTime: true)
      ),
      /*   Due Today   */
      equals: today(),
      then: a!map(
        icon: #"SYSTEM_SYSRULES_richTextIcon"(
          icon: "clock",
          size: ri!iconSize,
          color: ri!brandingMap.WarningAccentColor,
          caption: #"urn:appian:translation-string:v1:a252bcc9-689a-4c2a-a46a-fb5ca57a7ba9"
        ),
        text: #"urn:appian:translation-string:v1:492c0e0f-7195-43c0-ae1a-25047163feab"(
          #"urn:appian:translation-variable:v1:9cec6370-6bb2-4e54-a608-510c0259542f": rule!CMGT_UTIL_FormatDateTime(dateTime: ri!dueOn, returnTime: true)
        )
      ),
      /* Due Tomorrow     */
      equals: today() + 1,
      then: a!map(
        icon: #"SYSTEM_SYSRULES_richTextIcon"(
          icon: "clock",
          size: ri!iconSize,
          color: ri!brandingMap.WarningAccentColor,
          caption: #"urn:appian:translation-string:v1:a252bcc9-689a-4c2a-a46a-fb5ca57a7ba9"
        ),
        text: #"urn:appian:translation-string:v1:028d3aa9-fb98-4b9c-aca8-cf7afee3b18f"(
          #"urn:appian:translation-variable:v1:2ea8b884-d971-4045-8116-bd3eaddbe5dd": rule!CMGT_UTIL_FormatDateTime(dateTime: ri!dueOn, returnTime: true)
        )
      )
    )
  ),
  a!map(
    icon: #"SYSTEM_SYSRULES_richTextIcon"(
      icon: "calendar-o",
      size: ri!iconSize,
      color: ri!brandingMap.SecondaryTextColor
    ),
    text: a!defaultValue(
      value: rule!CMGT_UTIL_FormatDateTime(dateTime: ri!dueOn, includeTime: true),
      default: cons!CMGT_TXT_EMPTY_VALUE
    )
  )
)

_a-0000e98b-cfe0-8000-9ba5-011c48011c48_.sail_code:
rule!CMGT_UTIL_QueryRecord(
  recordType: recordType!CMGT_Case,
  returnType: ri!returnType,
  triggerRefresh: ri!triggerRefresh,
  executeWhen: ri!executeWhen,
  filters: {
    ri!additionalFilters,
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Case.caseId,
      operator: "in",
      value: ri!caseId
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Case.dueOn,
      operator: ">",
      value: rule!CMGT_UTIL_ConvertDatetoDatetime(
        date: ri!dueOnDate - 1,
        useSystemTimeZone: a!defaultValue(ri!useSystemTimeZoneForDueOn,true)
      )
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Case.dueOn,
      operator: "<=",
      value: rule!CMGT_UTIL_ConvertDatetoDatetime(
        date: ri!dueOnDate,
        useSystemTimeZone: a!defaultValue(ri!useSystemTimeZoneForDueOn,true)
      )
    )
  },
  logicalExpressions: ri!additionalLogicalExpression,
  fields: {
    ri!fields,
    if(
      ri!excludeRelatedRecords,
      {},
      {
        recordType!CMGT_Case.status,
        recordType!CMGT_Case.priority,
        recordType!CMGT_Case.caseType
      }
    ),
    if(
      ri!includeAssignees,
      {
        recordType!CMGT_Case.roles,
        recordType!CMGT_Case.roles
      },
      {}
    ),
    if(
      ri!includePrimaryContact,
      {
        recordType!CMGT_Case.caseEntityMap,
        #"urn:appian:record-relationship:v1:d81cdd0e-c54d-4b8e-beec-b7ae37fefe7f/9455b8a8-142d-4976-8b8d-83403eedad6a/043457e2-ee0b-4c7a-8210-8671bbe73128/2a2b64c2-0530-4e0a-a173-d26a396e3cc9"
      },
      {}
    )
  },
  relatedRecordData: {
    ri!relatedRecordData,
    if(
      ri!includePrimaryContact,
      #"SYSTEM_SYSRULES_relatedRecordData"(
        relationship: recordType!CMGT_Case.caseEntityMap,
        limit: 1,
        filters: #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_CaseEntityMap.entity.entityTypeId,
          operator: "=",
          value: cons!CMGT_REFID_ENTITY_TYPE_CONTACT
        )
      ),
      {}
    ),
    if(
      ri!includeAssignees,
      #"SYSTEM_SYSRULES_relatedRecordData"(
        relationship: recordType!CMGT_Case.roles,
        filters: #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_Role.roleTypeId,
          operator: "in",
          value: {
            cons!CMGT_REFID_ROLE_TYPE_USER_ASSIGNEE,
            cons!CMGT_REFID_ROLE_TYPE_GROUP_ASSIGNEE
          }
        )
      ),
      {}
    )
  },
  pagingInfo: ri!pagingInfo,
  sort: ri!sort,
  groupByFields: ri!groupByFields,
  searchTerm: ri!searchTerm,
  searchFields: ri!searchFields
)
