a!localVariables(
  local!wideScreen: {
    "TABLET_LANDSCAPE",
    "DESKTOP_NARROW",
    "DESKTOP",
    "DESKTOP_WIDE"
  },
  local!isWide: if(
    #"SYSTEM_SYSRULES_isPageWidth"(local!wideScreen),
    true,
    false
  ),
  local!searchTermTemp,
  local!brandingMap: rule!CMGT_LA_UTIL_LoadBrandingMap(),
  /*filter variables*/
  local!searchTerm,
  local!statusFilter,
  local!caseTypeFilter,
  local!pagingInfo: a!refreshVariable(
    value: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchSize: cons!CMGT_LA_INT_BATCH_SIZE_MEDIUM_PLUS,
      sort: #"SYSTEM_SYSRULES_sortInfo"(
        field: recordType!CMGT_Case.modifiedOn,
        ascending: false
      )
    ),
    refreshOnVarChange: {
      local!searchTerm,
      local!statusFilter,
      local!caseTypeFilter
    }
  ),
  /* This is relying on record security to define which cases the user can see */
  local!myCases: rule!CMGT_QR_GetCase(
    returnType: "DATA_SUBSET",
    pagingInfo: local!pagingInfo,
    triggerRefresh: now(),
    searchTerm: local!searchTerm,
    searchFields: {
      recordType!CMGT_Case.friendlyId,
      recordType!CMGT_Case.title,
      recordType!CMGT_Case.description
    },
    additionalFilters: {
      #"SYSTEM_SYSRULES_queryFilter"(
        field: recordType!CMGT_Case.statusId,
        operator: "in",
        value: local!statusFilter,
        applyWhen: a!isNotNullOrEmpty(local!statusFilter)
      )
    },
    fields: recordType!CMGT_Case.caseType.parentCaseType.name,
    additionalLogicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
      operator: "OR",
      filters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_Case.caseTypeUuid,
          operator: "in",
          value: local!caseTypeFilter,
          applyWhen: a!isNotNullOrEmpty(local!caseTypeFilter)
        ),
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_Case.caseType.parentUuid,
          operator: "in",
          value: local!caseTypeFilter,
          applyWhen: a!isNotNullOrEmpty(local!caseTypeFilter)
        )
      }
    )
  ),
  local!showEmptyState: rule!CMGT_QR_GetCase(returnType: "TOTAL_COUNT") = 0,
  rule!CMGT_LA_SitePages_HeaderLayout(
    title: #"urn:appian:translation-string:v1:fa639d31-3e8f-4da4-b79c-982832f88355",
    height: "SHORT_PLUS",
    contents: {
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            width: "EXTRA_NARROW",
            showWhen: local!isWide
          ),
          if(
            local!showEmptyState,
            /*No Active cases*/
            #"SYSTEM_SYSRULES_columnLayout"(
              contents: rule!CMGT_LA_EmptyState_NoActiveCases(brandingMap: local!brandingMap)
            ),
            /*Active cases*/
            #"SYSTEM_SYSRULES_columnLayout"(
              contents: {
                #"SYSTEM_SYSRULES_headingField"(
                  text: #"urn:appian:translation-string:v1:bada6c1b-b151-4719-9f79-90b09a8405d0",
                  color: local!brandingMap.StandardTextColor,
                  align: "START",
                  size: local!brandingMap.SecondaryHeaderSize,
                  fontWeight: local!brandingMap.SecondaryHeaderFontWeight
                ),
                /*Filters*/
                #"SYSTEM_SYSRULES_sideBySideLayout"(
                  items: {
                    /*Search filter*/
                    #"SYSTEM_SYSRULES_sideBySideItem"(
                      item: #"SYSTEM_SYSRULES_textField"(
                        label: #"urn:appian:translation-string:v1:8ca0ceb2-1d49-4c6a-a5d7-be892486fc90",
                        labelPosition: "COLLAPSED",
                        placeholder: #"urn:appian:translation-string:v1:8ca0ceb2-1d49-4c6a-a5d7-be892486fc90",
                        value: local!searchTermTemp,
                        saveInto: {
                          a!save(local!searchTermTemp, trim(save!value)),
                          if(
                            a!isNullOrEmpty(local!searchTermTemp),
                            a!save(local!searchTerm, local!searchTermTemp),
                            {}
                          )
                        },
                        refreshAfter: "KEYPRESS"
                      ),
                      width: "1X"
                    ),
                    #"SYSTEM_SYSRULES_sideBySideItem"(
                      item: #"SYSTEM_SYSRULES_ButtonArrayLayout"(
                        buttons: {
                          #"SYSTEM_SYSRULES_buttonWidget_v1"(
                            label: #"urn:appian:translation-string:v1:0b78090a-260f-4a0a-ad6b-c1a362918020",
                            saveInto: a!save(local!searchTerm, local!searchTermTemp),
                            style: "OUTLINE",
                            size: "SMALL"
                          )
                        },
                        align: "START",
                        marginBelow: "NONE"
                      ),
                      width: "MINIMIZE"
                    ),
                    /*Type filter*/
                    #"SYSTEM_SYSRULES_sideBySideItem"(
                      item: #"SYSTEM_SYSRULES_multipleDropdownField_v1"(
                        data: recordType!CMGT_Case,
                        choiceLabels: recordType!CMGT_Case.caseType.fullLabel,
                        choiceValues: recordType!CMGT_Case.caseType.uuid,
                        sort: #"SYSTEM_SYSRULES_sortInfo"(
                          field: recordType!CMGT_Case.caseType.fullLabel,
                          ascending: true()
                        ),
                        label: #"urn:appian:translation-string:v1:c19ba778-b22b-4d90-a4a6-669abde051ed",
                        labelPosition: "COLLAPSED",
                        placeholder: #"urn:appian:translation-string:v1:c19ba778-b22b-4d90-a4a6-669abde051ed",
                        value: local!caseTypeFilter,
                        saveInto: { local!caseTypeFilter }
                      ),
                      width: "1X"
                    ),
                    /*Status filter*/
                    #"SYSTEM_SYSRULES_sideBySideItem"(
                      item: #"SYSTEM_SYSRULES_multipleDropdownField_v1"(
                        data: #"SYSTEM_SYSRULES_recordData"(
                          recordType: recordType!CMGT_CFG_Status,
                          filters: {
                            #"SYSTEM_SYSRULES_queryFilter"(
                              field: recordType!CMGT_CFG_Status.isActive,
                              operator: "=",
                              value: true()
                            ),
                            #"SYSTEM_SYSRULES_queryFilter"(
                              field: recordType!CMGT_CFG_Status.type,
                              operator: "=",
                              value: "CASE"
                            )
                          }
                        ),
                        choiceLabels: recordType!CMGT_CFG_Status.statusLabel,
                        choiceValues: recordType!CMGT_CFG_Status.statusId,
                        label: #"urn:appian:translation-string:v1:2c464cfc-bcef-4865-b3af-504ebe888095",
                        labelPosition: "COLLAPSED",
                        placeholder: #"urn:appian:translation-string:v1:2c464cfc-bcef-4865-b3af-504ebe888095",
                        value: local!statusFilter,
                        saveInto: { local!statusFilter }
                      ),
                      width: "1X"
                    )
                  },
                  spacing: "STANDARD",
                  stackWhen: { "PHONE", "TABLET_PORTRAIT" },
                  marginBelow: "MORE"
                ),
                /*List of cases */
                if(
                  a!isNotNullOrEmpty(local!myCases.data),
                  #"SYSTEM_SYSRULES_cardGroupLayout"(
                    cards: #"SYSTEM_SYSRULES_forEach"(
                      items: local!myCases.data,
                      expression: #"SYSTEM_SYSRULES_cardLayout"(
                        contents: {
                          #"SYSTEM_SYSRULES_sectionLayout_v1"(
                            contents: {
                              rule!CMGT_LA_Cases_Sub_Details(
                                case: fv!item,
                                brandingMap: local!brandingMap
                              )
                            },
                            marginBelow: "LESS"
                          )
                        },
                        /*clicking the card navigates to case summary */
                        link: #"SYSTEM_SYSRULES_recordLink"(
                          label: rule!CMGT_UTIL_ReturnLabelGivenAutomationTestingSwitch(testingLabel: "Record Link" & fv!index),
                          recordType: recordType!CMGT_Case,
                          identifier: fv!item[recordType!CMGT_Case.caseId],
                          openLinkIn: "SAME_TAB"
                        ),
                        height: "AUTO",
                        style: local!brandingMap.CardBackgroundColor,
                        shape: local!brandingMap.CardShape,
                        showBorder: local!brandingMap.CardShowBorder,
                        showShadow: local!brandingMap.CardShowShadow
                      )
                    ),
                    spacing: "STANDARD",
                    cardWidth: if(
                      local!myCases.totalCount >= 3,
                      "NARROW_PLUS",
                      "MEDIUM_PLUS"
                    ),
                    marginBelow: "EVEN_MORE",
                    fillContainer: if(local!myCases.totalCount >= 3, true, false)
                  ),
                  #"SYSTEM_SYSRULES_cardLayout"(
                    contents: {
                      rule!CMGT_UTIL_DisplayEmptyState(
                        brandingMap: local!brandingMap,
                        primaryText: #"urn:appian:translation-string:v1:87aedbe2-2add-409e-86fb-b6bda768edc1",
                        docImage: local!brandingMap.NoResultsFoundImage,
                        imageSize: "SMALL"
                      )
                    },
                    height: "AUTO",
                    style: "TRANSPARENT",
                    shape: local!brandingMap.CardShape,
                    padding: "MORE",
                    marginBelow: "STANDARD",
                    showBorder: false,
                    showShadow: local!brandingMap.CasdShowBorder,
                    showWhen: local!myCases.totalCount = 0
                  ),

                ),
                /*Paging*/
                #"SYSTEM_SYSRULES_sectionLayout_v1"(
                  contents: rule!CMGT_UTIL_CustomPaging(
                    pagingInfo: local!pagingInfo,
                    totalCount: local!myCases.totalCount,
                    size: "MEDIUM",
                    brandingMap: local!brandingMap
                  ),
                  showWhen: and(
                    a!isNotNullOrEmpty(local!myCases.data),
                    local!myCases.totalCount > cons!CMGT_LA_INT_BATCH_SIZE_MEDIUM_PLUS
                  ),
                  marginAbove: "LESS",
                  marginBelow: "LESS"
                )
              }
            )
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            width: "EXTRA_NARROW",
            showWhen: local!isWide
          ),

        }
      )
    },
    description: #"urn:appian:translation-string:v1:87d3b9ef-65e7-455c-a4ad-1650b8dee99a",
    brandingMap: local!brandingMap,
    showHeader: true(),
    showActions: not(local!showEmptyState)
  )
)

