a!flatten(
  #"SYSTEM_SYSRULES_forEach"(
    items: ri!cases,
    expression: a!localVariables(
      /*Get the public folder for the case (the one with runtime security)*/
      local!folder: rule!CMGT_UTIL_GetSubFolderByType(
        parentFolderId: fv!item[recordType!CMGT_Case.folderId],
        folderType: cons!CMGT_TXT_CASE_SUB_FOLDERS[2]
      ),
      /*Get the current security*/
      local!folderSecurity: rule!CMGT_PLUGIN_ReturnContentRoleMap(folder: local!folder),
      local!currentEditorUsers: touser(
        a!flatten(local!folderSecurity.editorUsers)
      ),
      /*Determine all users who should be in the security role map*/
      /* 1) All roles for the case - roles will never be null*/
      local!userRolesForCase: fv!item[recordType!CMGT_Case.roles.username],
      local!usersWhoAreNotInternal: touser(
        reject(
          #"urn:appian:function:v1:a:isusermemberofgroup?okey=a!isUserMemberOfGroup"(
            username: _,
            groups: cons!CMGT_GRP_INTERNAL_USERS
          ),
          local!userRolesForCase
        )
      ),
      if(
        and(
          a!isNullOrEmpty(local!usersWhoAreNotInternal),
          a!isNullOrEmpty(local!currentEditorUsers)
        ),
        {},
        a!localVariables(
          /*Calculate the diff between current and desired security role map*/
          local!userEditorsToAddToFolder: difference(
            local!usersWhoAreNotInternal,
            local!currentEditorUsers
          ),
          local!usersToRemoveFromFolder: difference(
            local!currentEditorUsers,
            local!usersWhoAreNotInternal
          ),
          /*Create an output of modifications to make, if there are some to make*/
          if(
            and(
              a!isNullOrEmpty(local!userEditorsToAddToFolder),
              a!isNullOrEmpty(local!usersToRemoveFromFolder)
            ),
            {},
            {
              a!map(
                caseId: fv!item[recordType!CMGT_Case.caseId],
                folder: local!folder,
                editors: local!userEditorsToAddToFolder,
                removes: local!usersToRemoveFromFolder,
                admins: {},
                viewers: {}
              )
            }
          )
        )
      )
    )
  )
)

