rule!CMGT_UTIL_QueryRecord(
  recordType: recordType!CMGT_Case,
  returnType: ri!returnType,
  triggerRefresh: ri!triggerRefresh,
  executeWhen: ri!executeWhen,
  filters: {
    ri!additionalFilters,
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Case.caseId,
      operator: "in",
      value: ri!caseId
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Case.dueOn,
      operator: ">",
      value: rule!CMGT_UTIL_ConvertDatetoDatetime(
        date: ri!dueOnDate - 1,
        useSystemTimeZone: a!defaultValue(ri!useSystemTimeZoneForDueOn,true)
      )
    ),
    #"SYSTEM_SYSRULES_queryFilter"(
      field: recordType!CMGT_Case.dueOn,
      operator: "<=",
      value: rule!CMGT_UTIL_ConvertDatetoDatetime(
        date: ri!dueOnDate,
        useSystemTimeZone: a!defaultValue(ri!useSystemTimeZoneForDueOn,true)
      )
    )
  },
  logicalExpressions: ri!additionalLogicalExpression,
  fields: {
    ri!fields,
    if(
      ri!excludeRelatedRecords,
      {},
      {
        recordType!CMGT_Case.status,
        recordType!CMGT_Case.priority,
        recordType!CMGT_Case.caseType
      }
    ),
    if(
      ri!includeAssignees,
      {
        recordType!CMGT_Case.roles,
        recordType!CMGT_Case.roles
      },
      {}
    ),
    if(
      ri!includePrimaryContact,
      {
        recordType!CMGT_Case.caseEntityMap,
        #"urn:appian:record-relationship:v1:d81cdd0e-c54d-4b8e-beec-b7ae37fefe7f/9455b8a8-142d-4976-8b8d-83403eedad6a/043457e2-ee0b-4c7a-8210-8671bbe73128/2a2b64c2-0530-4e0a-a173-d26a396e3cc9"
      },
      {}
    )
  },
  relatedRecordData: {
    ri!relatedRecordData,
    if(
      ri!includePrimaryContact,
      #"SYSTEM_SYSRULES_relatedRecordData"(
        relationship: recordType!CMGT_Case.caseEntityMap,
        limit: 1,
        filters: #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_CaseEntityMap.entity.entityTypeId,
          operator: "=",
          value: cons!CMGT_REFID_ENTITY_TYPE_CONTACT
        )
      ),
      {}
    ),
    if(
      ri!includeAssignees,
      #"SYSTEM_SYSRULES_relatedRecordData"(
        relationship: recordType!CMGT_Case.roles,
        filters: #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_Role.roleTypeId,
          operator: "in",
          value: {
            cons!CMGT_REFID_ROLE_TYPE_USER_ASSIGNEE,
            cons!CMGT_REFID_ROLE_TYPE_GROUP_ASSIGNEE
          }
        )
      ),
      {}
    )
  },
  pagingInfo: ri!pagingInfo,
  sort: ri!sort,
  groupByFields: ri!groupByFields,
  searchTerm: ri!searchTerm,
  searchFields: ri!searchFields
)

