if(
  a!isNullOrEmpty(ri!parentFolderId),
  tofolder(null),
  tofolder(
    a!localVariables(
      local!allFolders: #"SYSTEM_SYSRULES_forEach"(
        items: {
          ri!parentFolderId,
          folder(ri!parentFolderId, "folderChildren")
        },
        expression: a!map(
          name: folder(fv!item, "name"),
          folderId: fv!item
        )
      ),
      local!filterForType: index(
        a!flatten(
          #"SYSTEM_SYSRULES_forEach"(
            items: local!allFolders,
            expression: if(
              search(ri!folderType, fv!item.name),
              fv!item.folderId,
              {}
            )
          )
        ),
        1,
        null
      ),
      a!defaultValue(local!filterForType, ri!parentFolderId)
    )
  )
)
