    cons!CMGT_LA_GRP_EXTERNAL_USERS
  )
)

_a-0000ecb5-acf8-8000-9cf6-011c48011c48_.sail_code:
a!localVariables(
  local!distinctBeforeTaskUUID: rule!CMGT_UTIL_Distinct(
    ri!caseTypeWorkflowTaskBranch[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.beforeCaseTypeWorkflowTaskUuid]
  ),
  cast(
    #"urn:appian:function:v1:a:listtype?okey=a!listType"(
      'type!{http://www.appian.com/ae/types/2009}Map'
    ),
    rule!CMGT_UTIL_RejectBlank(
      a!flatten(
        #"SYSTEM_SYSRULES_forEach"(
          items: local!distinctBeforeTaskUUID,
          expression: a!localVariables(
            local!thisUUID: fv!item,
            local!thisTaskBranches: rule!CMGT_UTIL_Filter(
              records: ri!caseTypeWorkflowTaskBranch,
              fieldValuePairs: {
                a!map(
                  field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.beforeCaseTypeWorkflowTaskUuid,
                  value: local!thisUUID
                ),

              }
            ),
            {
              a!match(
                value: tostring(
                  rule!CMGT_UTIL_Filter(
                    records: ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask],
                    field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid,
                    value: local!thisUUID
                  )[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTaskAction.actionCode]
                ),
                equals: cons!CMGT_CFG_TXT_ACT_WORKFLOW_ROUTING,
                /* Grouping the branches with same Condition Set Uuids */
                then: #"SYSTEM_SYSRULES_forEach"(
                  items: rule!CMGT_UTIL_Distinct(
                    local!thisTaskBranches[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.conditionSetUuid]
                  ),
                  expression: rule!CMGT_WFL_ConstructCaseTypeWorkflowTaskBranchMap(
                    taskUUID: local!thisUUID,
                    conditionSetUUID: fv!item,
                    afterCaseTypeWorkflowTaskBranches: rule!CMGT_UTIL_Filter(
                      records: local!thisTaskBranches,
                      field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.conditionSetUuid,
                      value: fv!item,

                    ),
                    isRequired: true,
                    isActive: true,
                    isRequirednessValid: true
                  )
                ),
                equals: cons!CMGT_CFG_TXT_ACT_SEND_EMAIL,
                /*   Grouping all the branches as the send email dependents are always required and valid      */
                then: rule!CMGT_WFL_ConstructCaseTypeWorkflowTaskBranchMap(
                  taskUUID: local!thisUUID,
                  afterCaseTypeWorkflowTaskBranches: local!thisTaskBranches,
                  isRequired: true,
                  isActive: true,
                  isRequirednessValid: true
                ),
                default: {}
              )
            }
          )
        )
      )
    )
  )
)

_a-0000ed5b-d85c-8000-9d42-011c48011c48_.sail_code:
/*Add the records and fields details that has to be displayed on case roles dropdown*/
/*Note : customFieldCode is mandatory for custom fields to uniquely identify the field
For Eg : User Assignee and Watcher refers to username field in CMGT_Roles table.
Hence it will be indentified using field code.*/
{
  /*Case Table*/
  a!map(
    recordTypeUuid: cons!CMGT_UUID_RECORD_TYPE_CASE,
    recordType: recordType!CMGT_Case,
    /*Add the field that will connect to the case record*/
    recordCaseField: recordType!CMGT_Case.caseId,
    /*Add the field UUIDs from the record that have to be included*/
    fieldMapsToAdd: {
      /*Created By*/
      a!map(
        uuid: "a5ede28b-dcb0-48e4-8a37-12ea5d27ee76",
        fieldName: "createdBy",
        displayName: #"urn:appian:translation-string:v1:d830cf7a-1b83-422b-b460-0058978459f0",
        taskSummaryDisplayName: #"urn:appian:translation-string:v1:fa6842b5-16c9-4121-b5f3-1290ad0408b6",
        field: recordType!CMGT_Case.createdBy,
        fieldType: "USERNAME",
        parentRecordTypeUuid: cons!CMGT_UUID_RECORD_TYPE_CASE
      )
    },
    customFieldsToAdd: {}
  ),
  /*Roles Table*/
  a!map(
    recordTypeUuid: "6799d796-883e-49ff-8975-297d87e40756",
    recordType: recordType!CMGT_Role,
    relationship: recordType!CMGT_Case.roles,
    recordCaseField: recordType!CMGT_Role.caseId,
    fieldMapsToAdd: {},
    customFieldsToAdd: {
      /*Case Assignee*/
      a!map(
        /*Username column in CMGT_Role*/
        uuid: "7576a553-bb91-4848-962a-c7a23a9f3b2d",
        displayName: #"urn:appian:translation-string:v1:d664ac0d-65d2-4e67-8187-2064e5beff4e",
        singularName: #"urn:appian:translation-string:v1:e4b1d453-add2-4441-a242-a5c4952c6f9e",
        taskSummaryDisplayName: #"urn:appian:translation-string:v1:7c774462-25e7-479e-b932-2937ce383559",
        taskSummarySingularName: #"urn:appian:translation-string:v1:16c222de-63d0-4437-96bd-732f479284af",
        customFieldCode: "CASE_ASSIGNEE",
        fieldType: "USERNAME",
        field: recordType!CMGT_Role.username,
        relationship: recordType!CMGT_Case.roles,
        relationshipType: "ONE_TO_MANY",
        extraConfigurations: a!map(
          evaluation: a!map(
            filters: {
              a!map(
                field: recordType!CMGT_Role.roleTypeId,
                operator: "=",
                value: cons!CMGT_REFID_ROLE_TYPE_USER_ASSIGNEE
              )
            }
          )
        ),
        parentRecordTypeUuid: cons!CMGT_UUID_RECORD_TYPE_CASE
      ),
      /*Group Assignee*/
      a!map(
        uuid: "980ec52a-b916-409f-9cdf-1752ce399f6d",
        displayName: #"urn:appian:translation-string:v1:e7a078ac-e54d-403d-9164-47a8dec13d00",
        singularName: #"urn:appian:translation-string:v1:9ef20f7e-d182-48ed-8b39-7a5219d71816",
        taskSummaryDisplayName: #"urn:appian:translation-string:v1:a3b5f8f8-3772-4a1a-ae64-1e5b78130764",
        taskSummarySingularName: #"urn:appian:translation-string:v1:cfd471e7-d1a6-450d-a8c8-5ed78dcc9976",
        field: recordType!CMGT_Role.groupId,
        customFieldCode: "GROUP_ASSIGNEE",
        relationship: recordType!CMGT_Case.roles,
        relationshipType: "ONE_TO_MANY",
        fieldType: "GROUP",
