a!localVariables(
  local!noOfResourcesInCurrentPage: length({ ri!resourcesToDisplay }),
  local!noOfAlignmentCards: abs(
    ri!batchSize - local!noOfResourcesInCurrentPage
  ),
  if(
    rule!CMGT_UTIL_IsNotBlank(input: ri!resourcesToDisplay),
    {
      /*Heading*/
      #"SYSTEM_SYSRULES_headingField"(
        text: #"urn:appian:translation-string:v1:aba40cfc-a3ef-4d2b-bcaf-4862d56a0e33"(
          #"urn:appian:translation-variable:v1:0aa82461-056b-44c3-834a-141269ffb0c4": ri!hierarchyLevel
        ),
        marginBelow: "NONE",
        marginAbove: "NONE",
        size: "SMALL",
        fontWeight: ri!brandingMap.SecondaryHeaderFontWeight,
        color: ri!brandingMap.StandardTextColor
      ),
      /*Resource Cards*/
      #"SYSTEM_SYSRULES_cardGroupLayout"(
        spacing: "DENSE",
        marginAbove: "LESS",
        marginBelow: if(
          ri!numberOfResources > ri!batchSize,
          "EVEN_LESS",
          "MORE"
        ),
        cards: {
          #"SYSTEM_SYSRULES_forEach"(
            items: ri!resourcesToDisplay,
            expression: rule!CMGT_KB_SingleResourceDisplay(
              resource: fv!item,
              brandingMap: ri!brandingMap,
              selectedResource: ri!selectedResource
            )
          ),
          /* Added these cards for alignment */
          if(
            local!noOfAlignmentCards = 0,
            {},
            #"SYSTEM_SYSRULES_forEach"(
              items: enumerate(local!noOfAlignmentCards),
              expression: #"SYSTEM_SYSRULES_cardLayout"(
                showBorder: false(),
                padding: "LESS",
                height: "AUTO",
                style: "TRANSPARENT"
              )
            )
          )
        },
        fillContainer: true,
        cardHeight: "AUTO"
      ),
      /*Pagination*/
      if(
        ri!numberOfResources > ri!batchSize,
        rule!CMGT_UTIL_CustomPaging(
          pagingInfo: ri!pagingInfo,
          totalCount: ri!numberOfResources,
          brandingMap: ri!brandingMap
        ),
        {}
      )
    },
    {}
  )
)

_a-0000ee62-d499-8000-9d9b-011c48011c48_.sail_code:
a!localVariables(
  local!brandingMap: rule!CMGT_CB_UTIL_LoadBrandingMap(),
  local!triggerRefresh: a!refreshVariable(
    value: now(),
    refreshAfter: "RECORD_ACTION"
  ),
  local!selectedCaseType: rule!CMGT_QR_GetCaseType(
    returnType: "SINGLE_OBJECT",
    caseTypeUuid: ri!selectedCaseTypeUuid,
    fields: {
      recordType!CMGT_CFG_CaseType.parentCaseType
    },
    triggerRefresh: local!triggerRefresh,
    includeInactive: true,
    executeWhen: a!isNotNullOrEmpty(ri!selectedCaseTypeUuid)
  ),
  local!allAvailableResourcesCount: rule!CMGT_KB_QR_GetResources(
    returnType: "TOTAL_COUNT",
    triggerRefresh: local!triggerRefresh,
    additionalLogicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
      operator: "OR",
      filters: {
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_KB_Resource.caseTypeUuid,
          operator: "is null"
        ),
        #"SYSTEM_SYSRULES_queryFilter"(
          field: recordType!CMGT_KB_Resource.caseTypeUuid,
          operator: "in",
          value: {
            ri!selectedCaseTypeUuid,
            local!selectedCaseType[recordType!CMGT_CFG_CaseType.parentUuid]
          },
          applyWhen: rule!CMGT_UTIL_IsNotBlank(ri!selectedCaseTypeUuid)
        )
      }
    ),
    fields: {
      recordType!CMGT_KB_Resource.caseType
    },
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchSize: cons!CMGT_INT_BATCH_SIZE_MAX,
      sort: #"SYSTEM_SYSRULES_sortInfo"(
        field: recordType!CMGT_KB_Resource.modifiedOn,
        ascending: false
      )
    )
  ),
  local!currentLevelSelected: a!match(
    value: ri!selectedCaseTypeUuid,
    equals: null,
    then: #"urn:appian:translation-string:v1:3807a409-6086-4767-8e1b-dd26b980ff5f",
    whenTrue: local!selectedCaseType[recordType!CMGT_CFG_CaseType.isCategory],
    then: local!selectedCaseType[recordType!CMGT_CFG_CaseType.name],
    whenTrue: local!selectedCaseType[recordType!CMGT_CFG_CaseType.isCategory] = false,
    then: local!selectedCaseType[recordType!CMGT_CFG_CaseType.name],
    default: #"urn:appian:translation-string:v1:3807a409-6086-4767-8e1b-dd26b980ff5f"
  ),
  local!displayConfigs: {
    a!map(
      hierarchyLevel: #"urn:appian:translation-string:v1:3807a409-6086-4767-8e1b-dd26b980ff5f"
    ),
    a!map(
      hierarchyLevel: if(
        toboolean(
          local!selectedCaseType[recordType!CMGT_CFG_CaseType.isCategory]
        ) = true(),
        local!selectedCaseType[recordType!CMGT_CFG_CaseType.name],
        local!selectedCaseType[recordType!CMGT_CFG_CaseType.parentCaseType.name]
      )
    ),
    a!map(
      hierarchyLevel: if(
        toboolean(
          local!selectedCaseType[recordType!CMGT_CFG_CaseType.isCategory]
        ) <> true(),
        local!selectedCaseType[recordType!CMGT_CFG_CaseType.name],
        null()
      )
    )
  },
  local!selectedResource,
  #"SYSTEM_SYSRULES_paneLayout"(
    panes: #"SYSTEM_SYSRULES_pane"(
      contents: {
        a!match(
          value: null,
          /* Summary of the resource */
          whenTrue: rule!CMGT_UTIL_IsNotBlank(local!selectedResource),
          then: rule!CMGT_KB_DisplayResource(
            selectedResource: local!selectedResource,
            brandingMap: local!brandingMap,
            triggerRefresh: local!triggerRefresh
          ),
          whenTrue: local!allAvailableResourcesCount = 0,
          /*Empty State*/
          then: #"SYSTEM_SYSRULES_cardLayout"(
            contents: {
              rule!CMGT_UTIL_DisplayEmptyState(
                brandingMap: local!brandingMap,
                primaryText: #"urn:appian:translation-string:v1:8d090952-9d52-491e-9218-3c1e254b9d43",
                secondaryText: #"urn:appian:translation-string:v1:34257d03-f106-4a39-9df7-9df147ead34f",
                docImage: cons!CMGT_KB_DOC_IMAGES_KNOWLEDGE_BASE_EMPTY_STATE,
                additionalContent: #"SYSTEM_SYSRULES_recordActionField_v1"(
                  actions: if(
                    a!isNotNullOrEmpty(ri!selectedCaseTypeUuid),
                    /*Add resource record action*/
                    #"SYSTEM_SYSRULES_recordActionItem"(
                      action: #"urn:appian:record-action:v1:82aa3b28-94c2-4d3c-a403-bb813bd76155/faa27aab-0728-4c54-a932-daa98dc7da5b",
                      identifier: ri!selectedCaseTypeUuid
                    ),
                    #"SYSTEM_SYSRULES_recordActionItem"(
                      action: #"urn:appian:record-action:v1:462e1408-1c19-4591-b946-fce9233909ce/42eaf4ed-12b3-4919-88b9-8f34e6a65d3d"
                    )
                  ),
                  align: "CENTER",
                  style: "TOOLBAR_PRIMARY",
                  display: "LABEL"
                ),
                primaryTextStyle: "STRONG",
                imageSize: "EXTRA_SMALL",
                primaryTextSize: "MEDIUM",
                secondaryTextMarginBelow: "MORE"
              )
            },
            height: "EXTRA_TALL",
            padding: "EVEN_MORE",
            marginAbove: "EVEN_MORE",
            showBorder: false,
            shape: local!brandingMap.CardShape,
            showShadow: local!brandingMap.CardShowShadow,
            style: local!brandingMap.CardBackgroundColor
          ),
          /*Display Resources*/
          default: rule!CMGT_KB_ResourcesCardDisplayWrapper(
            brandingMap: local!brandingMap,
            triggerRefresh: local!triggerRefresh,
            displayConfigs: local!displayConfigs,
            currentLevelSelected: local!currentLevelSelected,
            selectedResource: local!selectedResource,
            selectedCaseTypeUuid: ri!selectedCaseTypeUuid,
            selectedCaseType: local!selectedCaseType
          )
        )
      },
      backgroundColor: local!brandingMap.DefaultBackgroundColor
    )
  )
)

