a!localVariables(
  local!case: rule!CMGT_QR_GetCase(
    caseId: ri!task[recordType!CMGT_Task.caseId],
    fields: {
      recordType!CMGT_Case.caseType,
      recordType!CMGT_Case.caseType
    }
  ),
  local!roleData: rule!CMGT_EVAL_ReturnDataForCaseRole_SendEmailRecipients(
    recordTypeUuid: local!case[recordType!CMGT_Case.caseType.uuid],
    parentRecordTypeUuid: local!case[recordType!CMGT_Case.caseType.parentCaseType.uuid],
    caseId: ri!task[recordType!CMGT_Task.caseId]
  ),
  local!recipientRoleValues: #"SYSTEM_SYSRULES_forEach"(
    items: rule!CMGT_UTIL_ConstructRecipientsMap(
      recipientsJson: ri!task[recordType!CMGT_Task.sendEmailTask.recipientRoleJsonOnCompletion]
    ),
    expression: a!localVariables(
      local!filteredField: rule!CMGT_UTIL_Filter(
        records: local!roleData,
        fieldValuePairs: {
          a!map(
            field: "uuid",
            value: fv!item.uuid
          ),
          if(
            fv!item.customFieldCode = "NA",
            {},
            a!map(
              field: "customFieldCode",
              value: fv!item.customFieldCode
            )
          )
        }
      ),
      a!match(
        value: index(
          index(local!filteredField, 1, null),
          "fieldType",
          null
        ),
        equals:"USERNAME",
        then: toemailrecipient(touser(fv!item.value)),
        equals: "GROUP",
        then:a!localVariables(
          /*getting count of group members to check if there is atleast one member*/
          local!groupUsersCount: a!groupMembers(
            group: togroup(fv!item.value),
            memberType: "USER",
            pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 0)
          ).totalCount,
          if(
            local!groupUsersCount > 0,
            toemailrecipient(togroup(fv!item.value)),
            {}
          )
        ),
        default: toemailaddress(fv!item.value)
      )
    )
  ),
  local!recipientUserIds: #"SYSTEM_SYSRULES_forEach"(
    items: rule!CMGT_UTIL_ConstructRecipientsMap(
      recipientsJson: ri!task[recordType!CMGT_Task.sendEmailTask.recipientUserJson]
    ).id,
    expression: toemailrecipient(fv!item)
  ),
  local!recipientGroupIds: #"SYSTEM_SYSRULES_forEach"(
    items: rule!CMGT_UTIL_ConstructRecipientsMap(
      recipientsJson: ri!task[recordType!CMGT_Task.sendEmailTask.recipientGroupJson]
    ).id,
    expression: a!localVariables(
      /*getting count of group members to check if there is atleast one member*/
      local!groupUsersCount: a!groupMembers(
        group: togroup(fv!item),
        memberType: "USER",
        pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 0)
      ).totalCount,
      if(
        local!groupUsersCount > 0,
        toemailrecipient(fv!item),
        {}
      )
    )
  ),
  local!recipientEmailAddresses: #"SYSTEM_SYSRULES_forEach"(
    items: index(
      rule!CMGT_UTIL_ConstructRecipientsMap(
        recipientsJson: ri!task[recordType!CMGT_Task.sendEmailTask.recipientEmailJson]
      ),
      "id",
      null
    ),
    expression: toemailaddress(fv!item)
  ),
  local!allRecipients: a!flatten(
    rule!CMGT_UTIL_RejectBlank(
      array: {
        /*Emails entered in Email Text field    */
        local!recipientEmailAddresses,
        /*Users picked in User Picker */
        local!recipientUserIds,
        /*Groups picked in Group Picker */
        local!recipientGroupIds,
        /*Value picked in case roles dropdown */
        local!recipientRoleValues
      }
    )
  ),
  a!map(
    bccRecipients: if(
      rule!CMGT_UTIL_IsBlank(value: local!allRecipients),
      null,
      local!allRecipients
    ),
    attachments: rule!CMGT_UTIL_Filter(
      records: ri!task[recordType!CMGT_Task.sendEmailTask],
      field: recordType!CMGT_EmailTemplateAttachments.isActive,
      value: true
    )[recordType!CMGT_EmailTemplateAttachments.appianDocumentId],
    replacementParams: {
      find: "html",
      replace: ri!task[recordType!CMGT_Task.sendEmailTask.body]
    },
    subject: ri!task[recordType!CMGT_Task.sendEmailTask.subject],
    document: cons!CMGT_DOC_EMAIL_TEMPLATE_EMPTY
  )
)

