          ),
          #"SYSTEM_SYSRULES_forEach"(
            items: local!displayConfigs,
            expression: a!localVariables(
              local!rulesToDisplay: rule!CMGT_UTIL_Filter(
                records: fv!item.rulesToDisplay,
                field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.isActive,
                value: { true, if(fv!isLast, false, {}) }
              ),
              {
                rule!CMGT_WFL_CaseType_RulesCardDisplayWrapper(
                  label: fv!item.hierarchyLevel,
                  brandingMap: local!brandingMap,
                  subheaderLabel: fv!item.subHeadingLabel,
                  rulesToDisplay: local!rulesToDisplay,
                  showControls: fv!isLast,
                  triggerRefresh: local!triggerRefresh,
                  numOfHigherLevelRules: fv!item.numOfHigherLevelRules,
                  availableFields: local!filteredAvailableFields,
                  availableFieldsForTriggers: local!availableFieldsForTriggers
                )
