a!localVariables(
  local!studioParentRecordTypeUuid: a!defaultValue(
    ri!recordTypeUuid,
    ri!parentRecordTypeUuid
  ),
  local!controlPanelHierarchy: #"SYSTEM_SYSRULES_controlPanelRecordHierarchyMetadata"(
    controlPanel: #"urn:appian:control-panel:v1:1b82dc2b-d4df-44ed-ac7c-3cce3b4ee0e0",
    recordUuid: local!studioParentRecordTypeUuid
  ),

  /*Returns all the studio fields based on caseType record uuid & parent record uuid for relationship*/
  local!studioTextFields: rule!CMGT_UTIL_Filter(
    records: rule!CMGT_formatStudioFieldHierarchy(
      controlPanelHierarchy: local!controlPanelHierarchy
    ),
    fieldValuePairs: {
      a!map(field: "fieldType", value: "TEXT"),
      a!map(field: "fieldType", value: "LONG_TEXT")
    },
    matchingCriteria: "ANY"
  ),
  /*Only include studio fields that contain the word "email"*/
  local!studioEmailFields: a!flatten(
    #"SYSTEM_SYSRULES_forEach"(
      items: local!studioTextFields,
      expression: if(
        search("email", fv!item.displayName) <> 0,
        fv!item,
        {}
      )
    )
  ),
  local!availableFields: rule!CMGT_ENUM_AvailableFieldsForCaseRoles_SendEmailRecipients(),
  local!fieldsFromRecord: a!flatten(
    local!availableFields.fieldMapsToAdd
  ),
  local!customFieldsFromRecord: a!flatten(
    local!availableFields.customFieldsToAdd
  ),
  local!sortedFieldsFromRecord: rule!CMGT_UTIL_sortList(
    records: rule!CMGT_UTIL_RejectBlank(
      array: {
        local!fieldsFromRecord,
        local!customFieldsFromRecord
      }
    ),
    field: "displayName",
    ascending: true
  ),
  local!sortedStudioEmailFields: rule!CMGT_UTIL_sortList(
    records: local!studioEmailFields,
    field: "displayName",
    ascending: true
  ),
  if(
    ri!context = "COMPLETE_SEND_EMAIL_TASK",
    a!localVariables(
      local!case: rule!CMGT_QR_GetCase(
        caseId: ri!caseId,
        returnType: "SINGLE_OBJECT"
      ),
      local!studioData: rule!CMGT_QR_getStudioFieldsByCase(case: local!case),
      rule!CMGT_UTIL_RejectBlank(
        {
          /*  Fields From Record Data      */
          rule!CMGT_UTIL_sortList(
            records: rule!CMGT_UTIL_RejectBlank(
              array: a!flatten(
                #"SYSTEM_SYSRULES_forEach"(
                  items: local!availableFields,
                  expression: a!localVariables(
                    local!data: rule!CMGT_UTIL_QueryRecord(
                      recordType: fv!item.recordType,
                      filters: #"SYSTEM_SYSRULES_queryFilter"(
                        field: fv!item.recordCaseField,
                        operator: "=",
                        value: ri!caseId
                      )
                    ),
                    local!dataForFieldsFromRecord: a!flatten(
                      if(
                        rule!CMGT_UTIL_IsNotBlank(fv!item.fieldMapsToAdd),
                        #"SYSTEM_SYSRULES_forEach"(
                          items: fv!item.fieldMapsToAdd,
                          expression: #"urn:appian:function:v1:a:update"(
                            fv!item,
                            "value",
                            index(local!data, fv!item.field, null)
                          )
                        ),
                        {}
                      )
                    ),
                    local!dataForCustomFields: a!flatten(
                      if(
                        rule!CMGT_UTIL_IsNotBlank(fv!item.customFieldsToAdd),
                        #"SYSTEM_SYSRULES_forEach"(
                          items: fv!item.customFieldsToAdd,
                          expression: a!localVariables(
                            local!thisField: fv!item,
                            local!thisFilter:rule!CMGT_UTIL_DeepIndex(
                              item: local!thisField,
                              indexArray: {
                                "extraConfigurations",
                                "evaluation",
                                "filters"
                              }
                            ),
                            local!filteredValues: rule!CMGT_UTIL_Filter(
                              records: local!data,
                              field:local!thisFilter.field,
                              value: local!thisFilter.value
                            ),
                            if(
                              rule!CMGT_UTIL_IsBlank(local!filteredValues),
                              #"urn:appian:function:v1:a:update"(local!thisField, "value", ""),
                              #"SYSTEM_SYSRULES_forEach"(
                                items: rule!CMGT_UTIL_RejectBlank(
                                  index(
                                    local!filteredValues,
                                    local!thisField.field,
                                    null
                                  )
                                ),
                                expression: #"urn:appian:function:v1:a:update"(local!thisField, "value", fv!item)
                              ),

                            )
                          )
                        ),
                        {}
                      )
                    ),
                    {
                      local!dataForCustomFields,
                      local!dataForFieldsFromRecord
                    }
                  )
                ),

              )
            ),
            field: "displayName",
            ascending: true
          ),
          /*  Studio Text Field Data      */
          if(
            rule!CMGT_UTIL_IsNotBlank(local!sortedStudioEmailFields),
            #"SYSTEM_SYSRULES_forEach"(
              items: local!sortedStudioEmailFields,
              expression: #"urn:appian:function:v1:a:update"(
                fv!item,
                "value",
                a!defaultValue(
                  index(
                    local!studioData,
                    fv!item.dataKeyName,
                    null
                  ),
                  local!case
                )[fv!item.field]
              )
            ),
            {}
          )
        }
      )
    ),
    /*Return all the constructed map for recipients role dropdown*/
    rule!CMGT_UTIL_RejectBlank(
      append(
        local!sortedFieldsFromRecord,
        local!sortedStudioEmailFields
      )
    )
  )
)

