#"SYSTEM_SYSRULES_forEach"(
  items: ri!user,
  expression: a!localVariables(
    local!currentUserRecord: if(
      isusernametaken(fv!item),
      rule!CMGT_UTIL_Filter(
        records: ri!userRecord,
        field: #"urn:appian:record-field:v1:SYSTEM_RECORD_TYPE_USER/SYSTEM_RECORD_TYPE_USER_FIELD_username",
        value: fv!item
      ),
      null()
    ),
    cast(
      'type!{http://www.appian.com/ae/types/2009}Text',
      if(
        rule!CMGT_UTIL_IsNotBlank(input: local!currentUserRecord),
        if(
          or(
            local!currentUserRecord[#"urn:appian:record-field:v1:SYSTEM_RECORD_TYPE_USER/SYSTEM_RECORD_TYPE_USER_FIELD_isServiceAccount"] = true,
            #"urn:appian:function:v1:a:isusermemberofgroup?okey=a!isUserMemberOfGroup"(
              username: fv!item,
              groups: cons!CMGT_GRP_SERVICE_ACCOUNTS
            )
          ),
          cons!CMGT_TXT_AUTOMATED_SYSTEM,
          local!currentUserRecord[#"urn:appian:record-field:v1:SYSTEM_RECORD_TYPE_USER/SYSTEM_RECORD_TYPE_USER_FIELD_firstAndLastName"]
        ),
        if(
          isnull(fv!item),
          if(
            ri!returnNullForEmpty,
            null,
            cons!CMGT_TXT_EMPTY_VALUE
          ),
          fv!item
        )
      )
    )
  )
)

