83c4efee-b53d-4a10-8bac-fef3810bcb3c.sail_code:
with(
  /*All tasks that are currently not in the tree are considered available  */
  local!availableTasks: rule!CMGT_UTIL_Filter(
    filterType: "REJECT",
    records: ri!allTasks,
    field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid,
    value: ri!treeTasks[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid]
  ),
  /*The existing beforeTaskBranches in the tree */
  local!treeTaskBeforeBranches: rule!CMGT_UTIL_Filter(
    records: ri!caseTypeWorkflowTaskBranches,
    fieldValuePairs: {
      a!map(
        field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.afterCaseTypeWorkflowTaskUuid,
        value: ri!treeTasks[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid]
      ),
      a!map(
        field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.isRequired,
        value: true
      ),
      a!map(
        field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.isActive,
        value: true
      )
    }
  ),
  /*Identifies the UUIDs of the beforeTask in the tree branches  */
  local!previousTasksInTreeUuid: a!flatten(
    local!treeTaskBeforeBranches[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.beforeCaseTypeWorkflowTaskUuid]
  ),
  /*Finds the before task from the available list of tasks  */
  local!previousTasksInTree: rule!CMGT_UTIL_Filter(
    records: local!availableTasks,
    field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid,
    value: local!previousTasksInTreeUuid
  ),
  /*Add the identified tasks to the tree  */
  if(
    a!isNullOrEmpty(local!previousTasksInTree),
    ri!treeTasks,
    append(ri!treeTasks, local!previousTasksInTree)
  )
)

_a-0000e99c-1217-8000-9ba9-011c48011c48_.sail_code:
/*  Email Address Validation for Appian 7+  */
/*  Allows the 'name' to contain letters, numbers, plus period and special characters: _ - + ' & %  */
/*  Allows the 'domain' to contain only letters, numbers, period and dash.  */
/*When the case is created from the portal and if that email is used for account creation, only then below validation should be displayed in portal else existing validation should be displayed
Please enter a valid email address. Email address may only contain letters, numbers, and the following special characters: @ . _ - */
a!localVariables(
  local!validationMessage: #"urn:appian:translation-string:v1:b90bb29b-f3c4-4405-a032-741e69f0ed63",
  if(
    a!isNullOrEmpty(ri!textInput),
    null,
    a!localVariables(
      local!localPart: index(split(trim(ri!textInput), "@"), 1, {}),
      local!domainPart: index(split(trim(ri!textInput), "@"), 2, {}),
      {
        if(
          or(
            len(trim(ri!textInput)) > 255,
            length(split(trim(ri!textInput), " ")) > 1,
            count(split(ri!textInput, "@")) <> 2
          ),
          local!validationMessage,
          if(
            or(
              length(split(local!domainPart, ".")) < 2,
              contains(split(local!localPart, "."), ""),
              contains(split(local!domainPart, "."), ""),
            ),
            local!validationMessage,
            null
          )
        )
      }
    )
  )
)

_a-0000eb2c-b6af-8000-9c5d-011c48011c48_.sail_code:
/*
Note that because a forEach that loops over a single item is significantly less performant than just calling the thing,
we only want to use forEach here if multiple keys are passed, since this rule is used on every text on every interface
(many calls per page load).

Rather than create a separate rule that just adds to rule sprawl, we just commented the common blocks of identical code.
Updates to either one should be reflected in the other.
*/
a!localVariables(
  /*Using this to avoid re-evaluation on page interaction*/
  local!output: cast(
    /*Always ensure the key is returned as the same list-type of the input, especially for null/empty keys*/
    runtimetypeof(ri!key),
    if(
      a!isNullOrEmpty(ri!key),
      {},
      if(
        count(a!flatten(ri!key)) = 1,
        a!localVariables(
          local!key: a!flatten(ri!key)[1],
          /*START OF COMMON CODE*/
          local!rawLabel:a!defaultValue(ri!brandingMap[local!key], local!key),
          /*END OF COMMON CODE*/
          local!rawLabel
        ),
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!key,
          expression: a!localVariables(
            local!key: fv!item,
            /*START OF COMMON CODE*/
            local!rawLabel: a!defaultValue(ri!brandingMap[local!key], local!key),

            /*END OF COMMON CODE*/
            local!rawLabel
          )
        )
      )
    )
  ),
  local!output
)

_a-0000eb32-20f6-8000-9c60-011c48011c48_.sail_code:
cast(
  typeof(ri!records),
  if(
    a!isNullOrEmpty(ri!field),
    ri!records,
    with(
      local!dataSortedByField: todatasubset(
        ri!records,
        #"SYSTEM_SYSRULES_pagingInfo"(
          startIndex: 1,
          batchSize: - 1,
          sort: #"SYSTEM_SYSRULES_sortInfo"(
            field: ri!field,
            ascending: a!defaultValue(ri!ascending, true)
          )
        )
      ).data,
      if(
        a!isNullOrEmpty(ri!nullsFirst),
        local!dataSortedByField,
        with(
          local!nullsAggregated: rule!CMGT_UTIL_Filter(
            records: local!dataSortedByField,
            field: ri!field,
            value: null,
            filterType: "FULL_RESULT"
          ),
          if(
            ri!nullsFirst,
            append(
              local!nullsAggregated.matches,
              local!nullsAggregated.rejects
            ),
            append(
              local!nullsAggregated.rejects,
              local!nullsAggregated.matches
            )
          )
        )
      )
    )
  )
)

