if(
  rule!CMGT_UTIL_IsBlank(ri!caseTypeWorkFlowTasks),
  {},
  a!localVariables(
    /*Get Case Type WorkflowTask Branches*/
    local!caseTypeWorkFlowTaskBranches: a!flatten(
      ri!caseTypeWorkFlowTasks[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.beforeCaseTypeWorkflowTaskBranches]
    ),
    /*Filtering automation tasks*/
    local!workflowRoutingAutomationTasks: rule!CMGT_UTIL_Filter(
      records: rule!CMGT_UTIL_Filter(
        records: ri!caseTypeWorkFlowTasks,
        field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.taskBehaviorTypeId,
        value: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION
      ),
      field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTaskAction.actionCode,
