    ),
    if(
      and(
        rule!CMGT_UTIL_IsBlank(local!workflowRoutingAutomationTasks),
        rule!CMGT_UTIL_IsBlank(local!decisionTasks)
      ),
      {},
      rule!CMGT_UTIL_RejectBlank(
        {
