              local!currentTaskResponseOptions: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskResponseOptions],
              #"SYSTEM_SYSRULES_forEach"(
                items: local!currentTaskResponseOptions,
                expression: a!localVariables(
                  /*Filter the first task that each response option leads to and construct workflow path*/
                  local!taskBranchesOfCurrentResponseOption: rule!CMGT_UTIL_Filter(
                    records: local!caseTypeWorkFlowTaskBranches,
                    fieldValuePairs: {
                      a!map(
                        field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.beforeCaseTypeWorkflowTaskUuid,
                        value: local!currentTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid]
                      ),
                      a!map(
                        field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.caseTypeTaskResponseOptionUuid,
                        value: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskResponseOption.caseTypeWorkflowTaskResponseOptionUuid]
                      )
                    }
