              local!taskBranchesOfCurrentTask: rule!CMGT_UTIL_Filter(
                records: local!caseTypeWorkFlowTaskBranches,
                fieldValuePairs: {
                  a!map(
                    field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.beforeCaseTypeWorkflowTaskUuid,
                    value: local!currentTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid]
                  )
                }
              ),
              /*Construct workflow path for each task branch*/
              local!workflowPathsOfCurrentTaskLeadsToTask: #"SYSTEM_SYSRULES_forEach"(
                items: local!taskBranchesOfCurrentTask,
                expression: 'type!{urn:com:appian:types:CMGT_WFL}CMGT_WFL_CFG_CaseTypeTaskWorkflowPath'(
                  caseTypeUuid: ri!caseTypeUuid,
                  startConditionTaskUuid: local!currentTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid],
                  initialTaskUuid: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.afterCaseTypeWorkflowTaskUuid],
                  caseTypeTaskResponseOptionUuid: null,
                  conditionSetUuid: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.conditionSetUuid],
                  caseTypeWorkflowPathMapping: rule!CMGT_WFL_WorkflowPathMapping_ReturnWorkflowPathMappingsForGivenPath(
                    initialTaskUuid: fv!item[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTaskBranch.afterCaseTypeWorkflowTaskUuid],
                    caseTypeWorkflowTaskBranches: local!caseTypeWorkFlowTaskBranches
                  )
