a!localVariables(
  local!brandingMap: rule!CMGT_LA_UTIL_LoadBrandingMap(),
  local!wideScreen: {
    "TABLET_LANDSCAPE",
    "DESKTOP_NARROW",
    "DESKTOP",
    "DESKTOP_WIDE"
  },
  local!isWide: #"SYSTEM_SYSRULES_isPageWidth"(local!wideScreen),
  local!caseFriendlyId,
  local!personLastName,
  local!caseId,
  /* local!claimStatus - Stores any one of the below values
  CASE_NOT_FOUND - When there is no matching case found for the given friendlyId and lastname for the initiator
  CASE_ALREADY_CLAIMED - When the initiator has already claimed the case i.e. the initiator is a submitter
  CASE_CLAIMED - When the case is claimed successfully
  UNAUTHORIZED - When the initiator is not an External user
  MISSING_REQUIRED_PARAMTERS - When required paramters are not passed
  PROCESS_ERRORED - When the process errors to initiate

*/
  local!claimStatus,
  rule!CMGT_LA_SitePages_HeaderLayout(
    title: #"urn:appian:translation-string:v1:9f595600-9750-4407-a85c-3915147ac978",
    description: #"urn:appian:translation-string:v1:a75ce474-4a5c-4bb5-921f-d0d42d8247d8",
    showActions: false,
    contents: {
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          /*Space in the left hand side*/
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {},
            width: "EXTRA_NARROW",
            showWhen: local!isWide
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"SYSTEM_SYSRULES_cardLayout"(
                contents: {
                  #"SYSTEM_SYSRULES_columnsLayout"(
                    columns: {
                      #"SYSTEM_SYSRULES_columnLayout"(
                        contents: {},
                        width:if(#"SYSTEM_SYSRULES_isPageWidth"({"DESKTOP_WIDE","DESKTOP_WIDE"}) ,"NARROW_PLUS","EXTRA_NARROW"),
                        showWhen: local!isWide
                      ),
                      #"SYSTEM_SYSRULES_columnLayout"(
                        contents: {
                          #"SYSTEM_SYSRULES_textField"(
                            label: #"urn:appian:translation-string:v1:bdd65711-bfef-4bbd-8bc9-eaf4ee8fcf05",
                            labelPosition: "ABOVE",
                            placeholder: #"urn:appian:translation-string:v1:b1c76399-a211-4070-a17b-584bfd549cbc",
                            value: local!caseFriendlyId,
                            saveInto: {
                              a!save(local!caseFriendlyId, trim(save!value)),
                              a!save(local!claimStatus, null()),
                              a!save(local!caseId, null())
                            },
                            refreshAfter: "UNFOCUS",
                            characterLimit: cons!CMGT_INT_MAX_CHAR_LIMIT_MEDIUM,
                            showCharacterCount: false(),
                            required: true(),
                            requiredMessage:#"urn:appian:translation-string:v1:d6a18c3b-dd9c-4c1d-bd07-b3aac8255ebf"
                          ),
                          #"SYSTEM_SYSRULES_textField"(
                            label: #"urn:appian:translation-string:v1:18c3da99-a299-452c-aada-eef83059d7b7",
                            labelPosition: "ABOVE",
                            value: local!personLastName,
                            saveInto: {
                              a!save(local!personLastName, trim(save!value)),
                              a!save(local!claimStatus, null()),
                              a!save(local!caseId, null())
                            },
                            refreshAfter: "UNFOCUS",
                            characterLimit: cons!CMGT_INT_MAX_CHAR_LIMIT_MEDIUM,
                            showCharacterCount: false(),
                            required: true(),
                            requiredMessage: #"urn:appian:translation-string:v1:85a6ee2a-572e-4f6e-b953-bb67abd2565e",
                            validations: {}
                          ),
                          #"SYSTEM_SYSRULES_ButtonArrayLayout"(
                            buttons: {
                              #"SYSTEM_SYSRULES_buttonWidget_v1"(
                                label: #"urn:appian:translation-string:v1:a7f808d8-104d-44ce-b5df-4c7f6ab25fd1",
                                saveInto: {
                                  a!match(
                                    value: local!caseId,
                                    whenTrue: rule!CMGT_UTIL_IsNotBlank(fv!value),
                                    then: a!save(
                                      local!claimStatus,
                                      "CASE_ALREADY_CLAIMED"
                                    ),
                                    whenTrue: rule!CMGT_UTIL_IsBlank(local!claimStatus),
                                    then: rule!CMGT_SELF_INT_POST_ClaimCase(
                                      friendlyId: local!caseFriendlyId,
                                      personLastName: local!personLastName,
                                      initiator: loggedInUser(),
                                      onSuccess: {
                                        a!save(
                                          local!claimStatus,
                                          fv!result.body.claimStatus
                                        ),
                                        a!save(local!caseId, fv!result.body.caseId)
                                      },
                                      onError: a!save(
                                        local!claimStatus,
                                        fv!result.body.claimStatus
                                      )
                                    ),
                                    default: null()
                                  ),
                                  a!save(
                                    {
                                      local!caseFriendlyId,
                                      local!personLastName
                                    },
                                    null()
                                  )
                                },
                                style: "SOLID",
                                validate: true(),
                                width: "FILL"
                              )
                            },
                            align: "START",
                            marginAbove: "EVEN_LESS",
                            marginBelow: "STANDARD"
                          ),
                          rule!CMGT_SELF_BannerForClaimCase(
                            claimStatus: local!claimStatus,
                            brandingMap: local!brandingMap
                          )
                        },

                      ),
                      #"SYSTEM_SYSRULES_columnLayout"(
                        contents: {},
                        width:if(#"SYSTEM_SYSRULES_isPageWidth"({"DESKTOP_WIDE","DESKTOP_WIDE"}) ,"NARROW_PLUS","EXTRA_NARROW"),
                        showWhen: local!isWide
                      ),

                    },
                    stackWhen: { "PHONE", "TABLET_PORTRAIT" }
                  )
                },
                height: "AUTO",
                style: "TRANSPARENT",
                padding: "MORE",
                marginBelow: "STANDARD",
                showBorder: false(),
                showShadow: false()
              )
            }

          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {},
            width: "EXTRA_NARROW",
            showWhen: local!isWide
          )
        }
      ),
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          /*Space in the left hand side*/
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {},
            width: "EXTRA_NARROW",
            showWhen: local!isWide
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: rule!CMGT_LA_HomePage_FAQ(brandingMap: local!brandingMap),

          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {},
            width: "EXTRA_NARROW",
            showWhen: local!isWide
          )
        }
      )
    },
    brandingMap: local!brandingMap,
    height:"SHORT_PLUS"
  )
)
