sail_code:
concat(
  ri!pmName,
  " - ",
  tostring(ri!recordIdField),
  ": ",
  if(
    /*Can't use a!isNullOrEmpty() here to account for the `{null}` condition*/
    or(
      rule!CMGT_UTIL_IsBlank(ri!primaryRecordPv),
      rule!CMGT_UTIL_IsBlank(ri!primaryRecordPv[ri!recordIdField])
    ),
    "null/empty",
    joinarray(
      a!flatten(ri!primaryRecordPv[ri!recordIdField]),
      ", "
    )
  )
)
