sail_code:
/*If we need to hide a step, change showWhen to false()*/
a!localVariables(
  local!availableSteps: {
    /*Case Type*/
    a!map(
      formIndex: 1,
      label: #"urn:appian:translation-string:v1:c0d7d0b9-6396-43cf-bab5-11cfebcffa10",
      showWhen: true,
      showBaseButtons: true
    ),
    /*Additional Data*/
    a!map(
      formIndex: 2,
      label: #"urn:appian:translation-string:v1:7f3efa1c-9122-4c82-9864-2e3b41f3a091",
      showWhen: ri!showStudioData,
      showBaseButtons: false
    )
  },
  #"SYSTEM_SYSRULES_forEach"(
    items: rule!CMGT_UTIL_Filter(
      records: local!availableSteps,
      field: "showWhen",
      value: true()
    ),
    expression: #"urn:appian:function:v1:a:update"(
      data: fv!item,
      index: "index",
      value: fv!index
    )
  )
)
