sail_code:
if(
  a!isNotNullOrEmpty(ri!dateTime),
  a!localVariables(
    local!sysTimeZone: rule!CMGT_UTIL_SystemTimezone(),
    local!dateTimeInSystemTimeZone: rule!CMGT_UTIL_ConvertDateTimeToTimezone(
      dateTime: ri!dateTime,
      timezone: local!sysTimeZone
    ),
    local!dateInSystemTimeZone: rule!CMGT_UTIL_GetDateFromDateTime(
      dateTimeValue: local!dateTimeInSystemTimeZone
    ),
    /*Getting the next working date by adding ri!workingDays    */
    local!nextWorkingDateInSystemTimeZone: rule!CMGT_UTIL_AddBusinessDays(
      dateTime: local!dateTimeInSystemTimeZone,
      duration: a!defaultValue(value: ri!workingDays, default: 0)
    ),
    /*Adding the days difference to ri!dateTime    */
    ri!dateTime + (
      local!nextWorkingDateInSystemTimeZone - local!dateInSystemTimeZone
    )
  ),
  {}
)
