sail_code:
a!localVariables(
  local!faqQuestions: rule!CMGT_Portal_FAQData(),
  {
    #"SYSTEM_SYSRULES_headingField"(
      text: #"urn:appian:translation-string:v1:0f720bbb-8e0b-41d3-9d75-b2fbff186740",
      color: ri!brandingMap.StandardTextColor,
      align: "CENTER",
      size: ri!brandingMap.H2Size,
      fontWeight: ri!brandingMap.H2Weight
    ),
    #"SYSTEM_SYSRULES_cardLayout"(
      contents: {
        #"SYSTEM_SYSRULES_forEach"(
          items: local!faqQuestions,
          expression: a!localVariables(
            local!isCollapsed: true,
            {
              rule!CMGT_PortalPage_FAQCard(
                data: fv!item,
                brandingMap: ri!brandingMap,
                index: fv!index,
                isCollapsed: local!isCollapsed
              ),
              #"SYSTEM_SYSRULES_horizontalLine"(
                showWhen: fv!isLast <> true(),
                marginBelow: "NONE"
              )
            }
          )
        )
      },
      style: ri!brandingMap.SecondaryCardBackgroundColor,
      showBorder: false(),
      showShadow: ri!brandingMap.CardShowShadow,
      padding: "NONE",
      shape: ri!brandingMap.CardShape
    )
  }
)
