            label: rule!CMGT_UTIL_ReturnLabelGivenAutomationTestingSwitch(testingLabel: "Question" & ri!index),
            labelPosition: "COLLAPSED",
            value: {
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: ri!data.question,
                size: "STANDARD",
                color: ri!brandingMap.StandardTextColor
              )
            },
            align: "LEFT",
            marginAbove: "NONE",
            marginBelow: "NONE"
          ),
          width: "10X"
        ),
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: #"SYSTEM_SYSRULES_richTextDisplayField"(
            value: {
              #"SYSTEM_SYSRULES_richTextIcon"(
                icon: "plus-square-o",
                size: "STANDARD",
                color: ri!brandingMap.SecondaryTextColor
              )
            },
            align: "RIGHT",
            marginAbove: "NONE",
            marginBelow: "NONE"
          ),
          width: "1X",
          showWhen: ri!isCollapsed = true
        ),
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: #"SYSTEM_SYSRULES_richTextDisplayField"(
            value: {
              #"SYSTEM_SYSRULES_richTextIcon"(
                icon: "minus-square-o",
                size: "STANDARD",
                color: ri!brandingMap.SecondaryTextColor
              )
            },
            align: "RIGHT",
            marginAbove: "NONE",
            marginBelow: "NONE",

