sail_code:
a!localVariables(
  local!phoneScreen: { "TABLET_PORTRAIT", "PHONE" },
  local!isMobile: if(
    #"SYSTEM_SYSRULES_isPageWidth"(local!phoneScreen),
    true,
    false
  ),
  {
    /* Use the call to action pattern as a landing page */
    /* when your users have a single action to take */
    #"SYSTEM_SYSRULES_cardLayout"(
      /* Add whitespace above image */
      height: if(local!isMobile, "EXTRA_SHORT", "SHORT"),
      style: "TRANSPARENT",
      showBorder: false
    ),
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        /* Empty column layouts on both sides */
        /* help to center the content */
        #"SYSTEM_SYSRULES_columnLayout"(),
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            #"SYSTEM_SYSRULES_imageField_v1"(
              labelPosition: "COLLAPSED",
              images: {
                #"SYSTEM_SYSRULES_documentImage"(
                  document: if(
                    ri!isCaseSubmissionSuccessful,
                    ri!brandingMap.CaseConfirmationImage,
                    ri!brandingMap.CaseFailureImage
                  )
                )
              },
              size: "SMALL",
              align: "CENTER"
            ),
            #"SYSTEM_SYSRULES_richTextDisplayField"(
              labelPosition: "COLLAPSED",
              value: {
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: {
                    char(10),
                    if(
                      ri!isCaseSubmissionSuccessful,
                      #"urn:appian:translation-string:v1:db1d4ab7-3c78-4797-b429-f8d0c814d93d",
                      #"urn:appian:translation-string:v1:ea2539ee-4dd0-4f72-a5e3-84d0c454b6ba"
                    )
                  },
                  color: ri!brandingMap.PortalHeaderLabelColor,
                  size: "LARGE",
                  style: "STRONG"
                ),
                char(10),
                char(10),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: {
                    upper(
                      #"urn:appian:translation-string:v1:05f1177a-0ea3-4d28-ab8b-e8078f33d51c"(
                        #"urn:appian:translation-variable:v1:4d24e7c1-84e7-48de-af48-71a1c19fcc19": ri!case[recordType!CMGT_Case.friendlyId]
                      )
                    ),
                    char(10),
                    char(10)
                  },
                  showWhen: ri!isCaseSubmissionSuccessful,
                  color: ri!brandingMap.PortalHeaderLabelColor,
                  size: "MEDIUM"
                ),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: if(
                    ri!isCaseSubmissionSuccessful,
                    #"urn:appian:translation-string:v1:93bc9d53-d6b6-4d82-8ef2-6290f260fdcb",
                    #"urn:appian:translation-string:v1:3d4f9178-d9c0-49cb-a392-b68a75b0f594"
                  ),
                  size: "MEDIUM"
                ),
                char(10),
                char(10)
              },
              align: "CENTER"
            ),
            /* If your data is records-based, consider using a!recordActionField() */
            /* with CALL_TO_ACTION style instead of a!buttonArrayLayout() */
            #"SYSTEM_SYSRULES_columnsLayout"(
              columns: {
                #"SYSTEM_SYSRULES_columnLayout"(),
                #"SYSTEM_SYSRULES_columnLayout"(
                  contents: {
                    #"SYSTEM_SYSRULES_cardLayout"(
                      contents: {
                        #"SYSTEM_SYSRULES_richTextDisplayField"(
                          value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                            text: upper(
                              #"urn:appian:translation-string:v1:a27d6f43-5297-459d-94de-f42f6e7a8691"
                            ),
                            size: "STANDARD"
                          ),
                          preventWrapping: true,
                          align: "CENTER",
                          marginBelow: "EVEN_LESS"
                        )
                      },
                      link: #"SYSTEM_SYSRULES_safeLink"(
                        label: rule!CMGT_UTIL_ReturnLabelGivenAutomationTestingSwitch(testingLabel: "Back to Home"),
                        uri: ri!redirectUrl,
                        openLinkIn: "SAME_TAB"
                      ),
                      style: ri!brandingMap.AccentColor,
                      padding: "LESS",
                      showBorder: false
                    )
                  },
                  width: "NARROW"
                ),
                #"SYSTEM_SYSRULES_columnLayout"()
              }
            )
          },
          width: "WIDE"
        ),
        #"SYSTEM_SYSRULES_columnLayout"()
      },
      stackWhen: { "PHONE", "TABLET_PORTRAIT" }
    ),
    #"SYSTEM_SYSRULES_cardLayout"(
      /* Add whitespace below button */
      height: if(local!isMobile, "EXTRA_SHORT", "SHORT"),
      style: "TRANSPARENT",
      showBorder: false
    )
  }
)
