/*Only one not listed is tasks because they have separate sub processes that include audit capture*/
#"SYSTEM_SYSRULES_forEach"(
  items: ri!tasks,
  expression: a!localVariables(
    local!originalTask: rule!CMGT_UTIL_Filter(
      records: ri!originalTasks,
      field: recordType!CMGT_Task.taskInstanceUuid,
      value: fv!item[recordType!CMGT_Task.taskInstanceUuid]
    ),
    local!isNewOccurrence: and(
      rule!CMGT_UTIL_IsBlank(
        fv!item[recordType!CMGT_Task.taskId]
      ),
      tointeger(
        fv!item[recordType!CMGT_Task.instanceCount]
      ) > 1
    ),
    local!previousTaskOccurrence: rule!CMGT_UTIL_Filter(
      records: ri!originalTasks,
      field: recordType!CMGT_Task.taskUuid,
      value: fv!item[recordType!CMGT_Task.taskUuid]
    ),
    local!conditionSets: a!match(
      value: fv!item[recordType!CMGT_Task.taskBehaviorTypeId],
      equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION,
      then: if(
        rule!CMGT_UTIL_IsBlank(
          fv!item[recordType!CMGT_Task.conditionSet]
        ),
        rule!CMGT_QR_GetConditionSet(
          additionalFilters: #"SYSTEM_SYSRULES_queryFilter"(
            field: recordType!CMGT_ConditionSet.taskId,
            operator: "in",
            value: {
              fv!item[recordType!CMGT_Task.taskId]
            },
            applyWhen: rule!CMGT_UTIL_IsNotBlank(
              fv!item[recordType!CMGT_Task.taskId]
            )
          )
        ),
        fv!item[recordType!CMGT_Task.conditionSet]
      ),
      default: {}
    ),
    /*check on any conditions failed to evaluate in the automation rule*/
    local!inValidConditions: if(
      rule!CMGT_UTIL_IsNotBlank(local!conditionSets),
      rule!CMGT_UTIL_Filter(
        records: rule!CMGT_UTIL_Filter(
          records: a!flatten(
            local!conditionSets[recordType!CMGT_ConditionSet.childConditionSet]
          ),
          field: recordType!CMGT_ConditionSet.isActive,
          value: true
        )[recordType!CMGT_ConditionSet.condition],
        fieldValuePairs: {
          a!map(
            field: recordType!CMGT_Condition.isActive,
            value: true
          ),
          a!map(
            field: recordType!CMGT_Condition.isValid,
            value: false
          )
        }
      ),
      {}
    ),
    local!isInValidConditions: if(
      rule!CMGT_UTIL_IsNotBlank(local!inValidConditions),
      if(
        contains(
          touniformstring(
            #"SYSTEM_SYSRULES_forEach"(
              items: a!flatten(local!inValidConditions),
              expression: if(
                rule!CMGT_UTIL_IsNotBlank(
                  fv!item[recordType!CMGT_Condition.fieldPathJson]
                ),
                #"SYSTEM_SYSRULES_fromJson_v1"(
                  fv!item[recordType!CMGT_Condition.fieldPathJson]
                ).fieldType,
                false
              )
            )
          ),
          "CHOICE_LIST"
        ),
        true,
        false
      ),
      {}
    ),
    local!isAutomationTask: tointeger(
      fv!item[recordType!CMGT_Task.taskBehaviorTypeId]
    ) = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION,
    local!automationType: if(
      local!isAutomationTask,
      index(
        rule!CMGT_UTIL_Filter(
          records: rule!CMGT_CFG_ENUM_ACTIONS(),
          field: "actionCode",
          value: fv!item[recordType!CMGT_Task.automationTaskAction.actionCode]
        ),
        "name",
        null
      ),
      {}
    ),
    #"urn:appian:function:v1:a:update"(
      fv!item,
      recordType!CMGT_Task.eventHistory,
      a!flatten(
        {
          /*EVENTS THAT ARE PROCESS-SPECIFIC (based on "eventType" input)*/
          /*- For specific event types, look at the CMGT_CFG_EVENT_TYPE_TASK table*/
          a!match(
            value: ri!eventType,
            equals: "CREATE",
            then: rule!CMGT_TaskEventHistory_Constructor(
              taskId: fv!item[recordType!CMGT_Task.taskId],
              initiator: ri!initiator,
              eventTypeId: if(
                local!isAutomationTask,
                cons!CMGT_REFID_EVENT_TYPE_TASK_ADD_ACTIVITY,
                cons!CMGT_REFID_EVENT_TYPE_TASK_CREATE
              ),
              details: if(
                local!isAutomationTask,
                a!map(
                  automationType: local!automationType,
                  taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
                ),
                a!map(
                  taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
                )
              )
            ),
            equals: "OPEN",
            then: {
              if(
                local!isNewOccurrence,
                rule!CMGT_TaskEventHistory_Constructor(
                  taskId: fv!item[recordType!CMGT_Task.taskId],
                  initiator: ri!initiator,
                  eventTypeId: if(
                    local!isAutomationTask,
                    cons!CMGT_REFID_EVENT_TYPE_TASK_ADD_ACTIVITY,
                    cons!CMGT_REFID_EVENT_TYPE_TASK_CREATE
                  ),
                  details: if(
                    local!isAutomationTask,
                    a!map(
                      automationType: local!automationType,
                      taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
                    ),
                    a!map(
                      taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
                    )
                  )
                ),
                {}
              ),
              if(
                fv!item[recordType!CMGT_Task.statusId] = cons!CMGT_REFID_STATUS_TASK_READY,
                rule!CMGT_TaskEventHistory_Constructor(
                  taskId: fv!item[recordType!CMGT_Task.taskId],
                  initiator: ri!initiator,
                  eventTypeId: cons!CMGT_REFID_EVENT_TYPE_TASK_MARKED_ACTIVITY_AS_READY,
                  details: if(
                    local!isAutomationTask,
                    a!map(
                      automationType: local!automationType,
                      taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
                    ),
                    a!map(
                      taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
                    )
                  )
                ),
                {}
              )
            },
            equals: "OPEN_NEXT_TASK",
            then: {
              if(
                local!isNewOccurrence,
                rule!CMGT_TaskEventHistory_Constructor(
                  taskId: fv!item[recordType!CMGT_Task.taskId],
                  initiator: ri!initiator,
                  eventTypeId: if(
                    local!isAutomationTask,
                    cons!CMGT_REFID_EVENT_TYPE_TASK_ADD_ACTIVITY,
                    cons!CMGT_REFID_EVENT_TYPE_TASK_CREATE
                  ),
                  details: if(
                    local!isAutomationTask,
                    a!map(
                      automationType: local!automationType,
                      taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
                    ),
                    a!map(
                      taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
                    )
                  )
                ),
                {}
              ),
              if(
                fv!item[recordType!CMGT_Task.statusId] = cons!CMGT_REFID_STATUS_TASK_READY,
                rule!CMGT_TaskEventHistory_Constructor(
                  taskId: fv!item[recordType!CMGT_Task.taskId],
                  initiator: ri!initiator,
                  eventTypeId: cons!CMGT_REFID_EVENT_TYPE_TASK_MARKED_ACTIVITY_AS_READY,
                  details: if(
                    local!isAutomationTask,
                    a!map(
                      automationType: local!automationType,
                      taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
                    ),
                    a!map(
                      taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
                    )
                  )
                ),
                /*In smart workflow when the task(decision or automation) completed is looped to same task then
                all the tasks in the path will be reopened if already completed. So only the first task will be marked as
                ready and dependent task will be marked as queued*/
                if(
                  fv!item[recordType!CMGT_Task.statusId] = cons!CMGT_REFID_STATUS_TASK_QUEUED,
                  rule!CMGT_TaskEventHistory_Constructor(
                    taskId: fv!item[recordType!CMGT_Task.taskId],
                    initiator: ri!initiator,
                    eventTypeId: cons!CMGT_REFID_EVENT_TYPE_TASK_UPDATE_STATUS,
                    details: a!map(
                      value: rule!CMGT_QR_GetStatus(
                        statusId: fv!item[recordType!CMGT_Task.statusId],
                        fields: recordType!CMGT_CFG_Status.statusLabel
                      )[recordType!CMGT_CFG_Status.statusLabel],
                      taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
                    )
                  ),
                  {}
                )
              )
            },
            equals: "UPDATE_STATUS",
            then: rule!CMGT_TaskEventHistory_Constructor(
              taskId: fv!item[recordType!CMGT_Task.taskId],
              initiator: ri!initiator,
              eventTypeId: cons!CMGT_REFID_EVENT_TYPE_TASK_UPDATE_STATUS,
              details: a!map(
                value: fv!item[recordType!CMGT_Task.status.statusLabel],
                taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
              )
            ),
            equals: "SET_DUE_ON",
            then: rule!CMGT_TaskEventHistory_Constructor(
              taskId: fv!item[recordType!CMGT_Task.taskId],
              initiator: ri!initiator,
              eventTypeId: cons!CMGT_REFID_EVENT_TYPE_TASK_UPDATE_DUE_DATE,
              details: a!map(
                taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
              )
            ),
            equals: "UPDATE_NAME",
            then: rule!CMGT_TaskEventHistory_Constructor(
              taskId: fv!item[recordType!CMGT_Task.taskId],
              initiator: ri!initiator,
              eventTypeId: cons!CMGT_REFID_EVENT_TYPE_TASK_UPDATE_NAME,
              details: if(
                local!isAutomationTask,
                a!map(
                  originalValue: local!originalTask[recordType!CMGT_Task.title],
                  newValue: fv!item[recordType!CMGT_Task.title],
                  automationType: local!automationType,
                  taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
                ),
                a!map(
                  originalValue: local!originalTask[recordType!CMGT_Task.title],
                  newValue: fv!item[recordType!CMGT_Task.title],
                  taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
                )
              )
            ),
            equals: "COMPLETE",
            then: {
              if(
                local!isInValidConditions,
                rule!CMGT_TaskEventHistory_Constructor(
                  taskId: fv!item[recordType!CMGT_Task.taskId],
                  initiator: cons!CMGT_TXT_PROCESS_AUTOMATION,
                  eventTypeId: cons!CMGT_REFID_EVENT_TYPE_TASK_FAILED_TO_EVALUATE_RULE,
                  details: a!map(
                    value: #"urn:appian:translation-string:v1:960ea924-89e5-4006-bc5c-2719ad9d6a7e"
                  )
                ),
                {}
              ),
              rule!CMGT_TaskEventHistory_Constructor(
                taskId: fv!item[recordType!CMGT_Task.taskId],
                initiator: if(
                  local!isAutomationTask,
                  cons!CMGT_TXT_PROCESS_AUTOMATION,
                  ri!initiator
                ),
                eventTypeId: if(
                  fv!item[recordType!CMGT_Task.statusId] = cons!CMGT_REFID_STATUS_TASK_NOT_NEEDED,
                  cons!CMGT_REFID_EVENT_TYPE_TASK_NOT_NEEDED,
                  cons!CMGT_REFID_EVENT_TYPE_TASK_COMPLETE
                ),
                details: if(
                  fv!item[recordType!CMGT_Task.statusId] = cons!CMGT_REFID_STATUS_TASK_NOT_NEEDED,
                  if(
                    local!isAutomationTask,
                    a!map(
                      automationType: local!automationType,
                      taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
                    ),
                    a!map(
                      taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
                    )
                  ),
                  a!match(
                    value: fv!item[recordType!CMGT_Task.taskBehaviorTypeId],
                    equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_DECISION,
                    then: a!map(
                      taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label],
                      taskResponseOptionLabelKey: displayvalue(
                        fv!item[recordType!CMGT_Task.taskResponseOptionId],
                        fv!item[recordType!CMGT_Task.taskResponseOptions.taskResponseOptionId],
                        fv!item[recordType!CMGT_Task.taskResponseOptions.label],
                        null
                      )
                    ),
                    equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_DOCUMENT_UPLOAD,
                    then: a!map(
                      taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label],
                      value: joinarray(
                        #"SYSTEM_SYSRULES_forEach"(
                          items: fv!item[recordType!CMGT_Task.documents],
                          expression: document(
                            documentId: fv!item[recordType!CMGT_Document.appianDocId],
                            property: "name"
                          ) & "." & lower(
                            document(
                              documentId: fv!item[recordType!CMGT_Document.appianDocId],
                              property: "extension"
                            )
                          )
                        ),
                        ","
                      )
                    ),
                    equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_CREATE_CASE,
                    then: a!map(
                      taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label],
                      newCaseId: fv!item[recordType!CMGT_Task.taskCaseMap.caseId]
                    ),
                    equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_SEND_EMAIL,
                    then: a!map(
                      taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label],
                      subject: fv!item[recordType!CMGT_Task.sendEmailTask.subject]
                    ),
                    equals: cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION,
                    then: if(
                      fv!item[recordType!CMGT_Task.automationTaskAction.actionCode] = cons!CMGT_CFG_TXT_ACT_SEND_EMAIL,
                      a!map(
                        automationType: local!automationType,
                        taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label],
                        subject: fv!item[recordType!CMGT_Task.sendEmailTask.subject]
                      ),
                      a!map(
                        taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label],
                        automationType: local!automationType
                      )
                    ),
                    default: a!map(
                      taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
                    )
                  )
                )
              )
            },
            equals: "UPDATE",
            then: rule!CMGT_TaskEventHistory_Constructor(
              taskId: fv!item[recordType!CMGT_Task.taskId],
              initiator: ri!initiator,
              eventTypeId: cons!CMGT_REFID_EVENT_TYPE_TASK_UPDATE_ACTIVITY,
              details: if(
                local!isAutomationTask,
                a!map(
                  automationType: local!automationType,
                  taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
                ),
                a!map(
                  taskBehaviorType: fv!item[recordType!CMGT_Task.taskBehaviorType.label]
                )
              )
            ),
            equals: "MODIFY_WORKFLOW",
            then: if(
              local!isAutomationTask,
              rule!CMGT_Automation_ModifyWorkflowEventConstructor(
                task: fv!item,
                originalTask: local!originalTask,
                initiator: ri!initiator,
                newAutomationGoToBranches: ri!taskBranches,
                automationType: local!automationType
              ),
              rule!CMGT_Task_ModifyWorkflowEventConstructor(
                task: fv!item,
                originalTask: local!originalTask,
                initiator: ri!initiator,
                previousTaskOccurrence: local!previousTaskOccurrence,
                isNewOccurrence: local!isNewOccurrence
              )
            ),
            equals: "INFINITE_AUTOMATION_EXIT",
            then: rule!CMGT_TaskEventHistory_Constructor(
              taskId: fv!item[recordType!CMGT_Task.taskId],
              initiator: ri!initiator,
              eventTypeId: cons!CMGT_REFID_EVENT_TYPE_TASK_WORKFLOW_EXIT,
              details: a!map(
                value: #"urn:appian:translation-string:v1:32779755-d4b4-423a-91e8-1921abf7710e"(
                  #"urn:appian:translation-variable:v1:5ad77ebe-519e-4430-8581-788799022117": local!automationType,
                  #"urn:appian:translation-variable:v1:408566a5-8a3b-4d35-82e5-98b62c733069": cons!CMGT_INT_MAX_NUMBER_OF_AUTOMATION_EXECUTIONS
                )
              )
            ),
            equals: "UPDATE_EMAIL",
            then: rule!CMGT_TaskEventHistory_Constructor(
              taskId: fv!item[recordType!CMGT_Task.taskId],
              initiator: ri!initiator,
              details: a!map(
                subject: fv!item[recordType!CMGT_Task.sendEmailTask.subject]
              ),
              eventTypeId: cons!CMGT_REFID_EVENT_TYPE_TASK_UPDATE_EMAIL
            ),
            default: {}
          ),
          /*EVENTS THAT ARE NON-PROCESS-SPECIFIC (based on "what children are populated")*/
          /* - Roles*/
          rule!CMGT_Role_SetEvents(
            roles: fv!item[recordType!CMGT_Task.roles],
            initiator: ri!initiator,
            context: "TASK",
            originalRoles: local!originalTask[recordType!CMGT_Task.roles]
          ),
          /*- Documents*/
          rule!CMGT_Document_SetEvents(
            documents: fv!item[recordType!CMGT_Task.documents],
            initiator: ri!initiator
          ),
          /* - Comments*/
          rule!CMGT_Comment_SetEvents(
            comments: fv!item[recordType!CMGT_Task.comments],
            initiator: ri!initiator,
            context: "TASK"
          ),

        }
      )
    )
  )
)
