sail_code:
a!localVariables(
  local!brandingMap: rule!CMGT_CB_UTIL_LoadBrandingMap(),
  #"SYSTEM_SYSRULES_formLayout_v2"(
    titleBar: #"SYSTEM_SYSRULES_cardLayout"(
      contents: {
        #"SYSTEM_SYSRULES_sideBySideLayout"(
          items: {
            #"SYSTEM_SYSRULES_sideBySideItem"(
              item: #"SYSTEM_SYSRULES_stampField"(
                icon: "files-o",
                contentColor: local!brandingMap.StandardStampContentColor,
                backgroundColor: local!brandingMap.StandardStampBackgroundColor,
                size: "TINY",
                shape: local!brandingMap.StandardStampShape
              ),
              width: "MINIMIZE"
            ),
            #"SYSTEM_SYSRULES_sideBySideItem"(
              item: #"SYSTEM_SYSRULES_headingField"(
                text: #"urn:appian:translation-string:v1:fbae5b74-85c1-4f6e-b225-90719ad331d4"(
                  #"urn:appian:translation-variable:v1:06ddcf4b-d22a-43c9-85d0-ad5890d04115": ri!taskBlock[recordType!CMGT_WFL_CFG_TaskBlock.label]
                ),
                size: local!brandingMap.H1Size,
                headingTag: "H1",
                fontWeight: "SEMI_BOLD",
                marginAbove: "STANDARD"
              )
            )
          },
          alignVertical: "MIDDLE",
          spacing: "STANDARD"
        )
      },
      padding: "STANDARD",
      showBorder: false()
    ),
    showTitleBarDivider: true(),
    contents: {
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              rule!CMGT_UTIL_DeleteConfirmation(
                primaryMessage: #"urn:appian:translation-string:v1:a554509d-b989-4dac-b93a-ebadc397f6cf",
                secondaryMessage: #"urn:appian:translation-string:v1:c1d85518-d2f2-4df2-a6d6-d45c881666db",
                stampIcon: "WARNING",
                brandingMap: local!brandingMap,
                stampColor: local!brandingMap.WarningAccentColor
              )
            }
          ),

        }
      )
    },
    isButtonFooterFixed: true,
    showButtonDivider: true,
    buttons: #"SYSTEM_SYSRULES_ButtonLayout"(
      primaryButtons: {
        #"SYSTEM_SYSRULES_buttonWidget_v1"(
          label: #"urn:appian:translation-string:v1:7de22116-f1fc-4986-a6ab-959d1b2c5eca",
          submit: true,
          style: "SOLID"
        )
      },
      secondaryButtons: {
        #"SYSTEM_SYSRULES_buttonWidget_v1"(
          label: #"urn:appian:translation-string:v1:edd4df67-d878-46d6-ab67-732a3700a588",
          saveInto: a!save(ri!taskBlock, null),
          submit: true,
          style: "OUTLINE",
          validate: false
        )
      }
    )
  )
)
