sail_code:
a!localVariables(
  local!wideScreen: {
    "TABLET_LANDSCAPE",
    "DESKTOP_NARROW",
    "DESKTOP",
    "DESKTOP_WIDE"
  },
  local!isWide: if(
    #"SYSTEM_SYSRULES_isPageWidth"(local!wideScreen),
    true,
    false
  ),
  local!isCreationSuccessful,
  local!portalConfig: rule!CMGT_INT_GET_PortalConfig(locale: "en_US"),
  local!brandingMap: local!portalConfig.result.body.brandingMap,
  local!portalUrl: local!portalConfig.result.body.urlInfo.portalPath,
  local!userDetails: a!map(
    firstName: null,
    lastName: null,
    email: null
  ),
  if(
    a!isNotNullOrEmpty(local!isCreationSuccessful),
    /*Confirmation Message*/
    #"SYSTEM_SYSRULES_headerContentLayout"(
      contents: #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {},
            width: if(local!isWide, "NARROW_PLUS", "AUTO"),
            showWhen: not(
              #"SYSTEM_SYSRULES_isPageWidth"(
                pageWidths: {
                  "PHONE",
                  "TABLET_PORTRAIT",
                  "TABLET_LANDSCAPE",
                  "DESKTOP_NARROW"
                }
              )
            )
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"SYSTEM_SYSRULES_cardLayout"(
                contents: {
                  #"SYSTEM_SYSRULES_imageField_v1"(
                    labelPosition: "COLLAPSED",
                    images: #"SYSTEM_SYSRULES_documentImage"(
                      local!brandingMap.CaseConfirmationImage
                    ),
                    size: "SMALL",
                    align: "CENTER"
                  ),
                  #"SYSTEM_SYSRULES_richTextDisplayField"(
                    labelPosition: "COLLAPSED",
                    value: {
                      #"SYSTEM_SYSRULES_richTextItem_v1"(
                        text: {
                          #"urn:appian:translation-string:v1:3f90e0f6-3e3c-469b-b018-1b071de40a53"
                        },
                        color: local!brandingMap.StandardTextColor,
                        size: "MEDIUM_PLUS"
                      )
                    },
                    align: "CENTER",
                    marginBelow: "LESS"
                  ),
                  #"SYSTEM_SYSRULES_richTextDisplayField"(
                    labelPosition: "COLLAPSED",
                    value: {
                      #"SYSTEM_SYSRULES_richTextItem_v1"(
                        text: {
                          #"urn:appian:translation-string:v1:acf70357-a4a4-4ed1-a6f0-748e683fbefb"
                        },
                        color: local!brandingMap.SecondaryTextColor,
                        size: "MEDIUM"
                      )
                    },
                    align: "CENTER"
                  ),
                  #"SYSTEM_SYSRULES_richTextDisplayField"(
                    value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                      text: upper(
                        #"urn:appian:translation-string:v1:be712641-a44b-4163-907e-c1daacb4a9dd"
                      ),
                      link: #"SYSTEM_SYSRULES_safeLink"(
                        label: rule!CMGT_UTIL_ReturnLabelGivenAutomationTestingSwitch(testingLabel: "Home"),
                        uri: local!portalUrl & "/page/" & cons!CMGT_SELF_PORTAL_TXT_HOME_URL_STUB,
                        openLinkIn: "SAME_TAB"
                      ),
                      linkStyle: "STANDALONE",
                      size: "MEDIUM"
                    ),
                    align: "CENTER"
                  )
                },
                height: "AUTO",
                style: local!brandingMap.CardBackgroundColor,
                shape: local!brandingMap.CardShape,
                padding: "STANDARD",
                marginBelow: "NONE",
                showBorder: local!brandingMap.CardShowBorder,
                showShadow: local!brandingMap.CardShowShadow
              )
            }
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {},
            width: if(local!isWide, "NARROW_PLUS", "AUTO"),
            showWhen: not(
              #"SYSTEM_SYSRULES_isPageWidth"(
                pageWidths: {
                  "PHONE",
                  "TABLET_PORTRAIT",
                  "TABLET_LANDSCAPE",
                  "DESKTOP_NARROW"
                }
              )
            )
          )
        },
        marginAbove: "EVEN_MORE"
      ),
      showWhen: a!isNotNullOrEmpty(local!isCreationSuccessful),
      backgroundColor: local!brandingMap.DefaultBackgroundColor
    ),
    #"SYSTEM_SYSRULES_paneLayout"(
      panes: {
        #"SYSTEM_SYSRULES_pane"(
          contents: {
            #"SYSTEM_SYSRULES_columnsLayout"(
              columns: {
                #"SYSTEM_SYSRULES_columnLayout"(
                  contents: {
                    #"SYSTEM_SYSRULES_billboardLayout_v1"(
                      backgroundMedia: #"SYSTEM_SYSRULES_documentImage"(
                        document: cons!CMGT_SELF_DOC_IMAGE_SIGN_UP_PAGE_BANNER
                      ),
                      marginBelow: "NONE",
                      height: "AUTO",
                      backgroundMediaPositionVertical: "TOP"
                    )
                  },
                  width: if(
                    #"SYSTEM_SYSRULES_isPageWidth"(
                      pageWidths: {
                        "DESKTOP_WIDE",
                        "DESKTOP",
                        "DESKTOP_NARROW"
                      }
                    ),
                    "MEDIUM",
                    "NARROW_PLUS"
                  ),
                  showWhen: local!isWide
                ),
                #"SYSTEM_SYSRULES_columnLayout"(
                  contents: {
                    #"SYSTEM_SYSRULES_columnsLayout"(
                      columns: {
                        #"SYSTEM_SYSRULES_columnLayout"(
                          width: if(
                            #"SYSTEM_SYSRULES_isPageWidth"(pageWidths: { "DESKTOP_WIDE", "DESKTOP" }),
                            "NARROW",
                            "EXTRA_NARROW"
                          ),
                          showWhen: local!isWide
                        ),
                        #"SYSTEM_SYSRULES_columnLayout"(
                          contents: #"SYSTEM_SYSRULES_cardLayout"(
                            contents: {
                              #"SYSTEM_SYSRULES_imageField_v1"(
                                images: #"SYSTEM_SYSRULES_documentImage"(document: local!brandingMap.SiteLogo),
                                align: "CENTER",
                                size: "SMALL_PLUS",
                                showWhen: not(local!isWide),

                              ),
                              #"SYSTEM_SYSRULES_richTextDisplayField"(showWhen: not(local!isWide)),
                              #"SYSTEM_SYSRULES_richTextDisplayField"(showWhen: not(local!isWide)),
                              rule!CMGT_SELF_SignUpDetails(
                                brandingMap: local!brandingMap,
                                userDetails: local!userDetails,
                                isCreationSuccessful: local!isCreationSuccessful,
                                portalConfig: local!portalConfig
                              )
                            },
                            height: "AUTO",
                            style: "TRANSPARENT",
                            shape: "SEMI_ROUNDED",
                            padding: "EVEN_MORE",
                            marginBelow: "STANDARD",
                            marginAbove: "EVEN_MORE",
                            showBorder: false
                          )
                        ),
                        #"SYSTEM_SYSRULES_columnLayout"(
                          width: if(
                            #"SYSTEM_SYSRULES_isPageWidth"(pageWidths: { "DESKTOP_WIDE", "DESKTOP" }),
                            "NARROW",
                            "EXTRA_NARROW"
                          ),
                          showWhen: local!isWide
                        )
                      }
                    )
                  }
                )
              }
            )
          },
          backgroundColor: local!brandingMap.DefaultBackgroundColor,
          padding: "NONE"
        )
      },
      showWhen: a!isNullOrEmpty(local!isCreationSuccessful),

    )
  )
)
