sail_code:
a!localVariables(
  local!email: user(loggedInUser(), "email"),
  #"SYSTEM_SYSRULES_formLayout_v1"(
    label: #"urn:appian:translation-string:v1:e3abfa3b-e5b1-4e81-940f-76163c51d1d7",
    contents: {
      #"SYSTEM_SYSRULES_textField"(
        label: #"urn:appian:translation-string:v1:bedad1e1-9c55-42d6-9502-7c93bb6fa546",
        labelPosition: "ABOVE",
        value: local!email,
        saveInto: { a!save(local!email, trim(save!value)) },
        refreshAfter: "UNFOCUS",
        required: true,
        validations: {
          rule!CMGT_UTIL_ValidateEmail(textInput: local!email)
        },
        inputPurpose: "EMAIL"
      )
    },
    buttons: #"SYSTEM_SYSRULES_ButtonLayout"(
      primaryButtons: {
        #"SYSTEM_SYSRULES_buttonWidget_v1"(
          label: #"urn:appian:translation-string:v1:fd2c9c43-8792-4bb8-8c95-a7e10cd42b45",
          saveInto: {
            a!save(ri!email, local!email),
            a!save(
              ri!testUserNames,
              cons!CMGT_EXT_MAXIMUM_DATA_USERS
            )
          },
          submit: true,
          style: "SOLID"
        )
      },
      secondaryButtons: {
        #"SYSTEM_SYSRULES_buttonWidget_v1"(
          label: #"urn:appian:translation-string:v1:7e8dd731-f9a6-40e2-9e39-49f41efa8702",
          value: true,
          saveInto: a!save(ri!email, null()),
          submit: true,
          validate: false
        )
      }
    )
  )
)
