sail_code:
if(
  ri!isAnyInvalidCondition > 0,
  #"SYSTEM_SYSRULES_richTextDisplayField"(
    labelPosition: "COLLAPSED",
    value: {
      #"SYSTEM_SYSRULES_richTextIcon"(
        icon: "warning",
        linkStyle: "STANDALONE",
        color: ri!brandingMap.WarningAccentColor,
        size: "MEDIUM"
      ),
      char(32),
      #"SYSTEM_SYSRULES_richTextItem_v1"(
        text: #"urn:appian:translation-string:v1:b2fa9c04-a5a2-4d27-9a30-8d6774fe5b2d"
      )
    },
    align: "LEFT",
    marginBelow: "LESS"
  ),
  {
    #"SYSTEM_SYSRULES_forEach"(
      items: ri!conditions,
      expression: {
        #"SYSTEM_SYSRULES_richTextDisplayField"(
          labelPosition: "COLLAPSED",
          marginBelow: "NONE",
          value:{
            #"SYSTEM_SYSRULES_richTextItem_v1"(
              text: #"urn:appian:translation-string:v1:b767a631-0a07-423f-9bf1-fa6b6bd96ddc"
            ),
            " ",
            rule!CMGT_Automation_FormatCondition(
              fieldPathJson: fv!item[recordType!CMGT_Condition.fieldPathJson],
              textSize:"STANDARD",
              valueJson: fv!item[recordType!CMGT_Condition.valueJson],
              operator: fv!item[recordType!CMGT_Condition.operator],
              fieldDetails: ri!fieldDetails
            )
          }),
          #"SYSTEM_SYSRULES_richTextDisplayField"(
            marginAbove: "LESS",
            value: {
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: ri!conditionSet[recordType!CMGT_ConditionSet.operatorCode],
                style: "STRONG",
                showWhen: fv!index < count(ri!conditions)
              )
            }
          )
      }
    )
  }
)
