/*List of Dependents*/
#"SYSTEM_SYSRULES_forEach"(
  items: ri!taskBranchMaps[ri!afterTaskBranchMapIndices],
  expression: a!localVariables(
    local!activeAfterTaskBranches_thisTask: rule!CMGT_UTIL_Filter(
      records: a!flatten(
        ri!taskBranchMaps[ri!afterTaskBranchMapIndices].afterTaskBranches
      ),
      fieldValuePairs: {
        a!map(
          field: recordType!CMGT_TaskBranch.isActive,
          value: false
        ),
        a!map(
          field: recordType!CMGT_TaskBranch.afterTaskInstanceUuid,
          value: null
        )
      },
      matchingCriteria: "ANY",
      filterType: "REJECT"
    ),
    /*For the selected task in the middle pane*/
    /*Each respone in decision task can be connected to multiple tasks. So we will be rejecting only
                                the dependent tasks for current response option of the selected task.
                                For non - Decision task we need to reject all active task dependents of current task*/
    /*For the selected task in the middle pane*/
    local!afterTasks: if(
      tointeger(ri!taskBehaviorTypeId) = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_DECISION,
      rule!CMGT_UTIL_Filter(
        records: ri!case[recordType!CMGT_Case.tasks][ri!visibleTaskIndices],
        field: recordType!CMGT_Task.taskInstanceUuid,
        value: rule!CMGT_UTIL_Filter(
          records: local!activeAfterTaskBranches_thisTask,
          field: recordType!CMGT_TaskBranch.taskResponseOptionUuid,
          value: fv!item.taskResponseOptionUUID
        )[recordType!CMGT_TaskBranch.afterTaskInstanceUuid]
      ),
      rule!CMGT_UTIL_Filter(
        records: ri!case[recordType!CMGT_Case.tasks][ri!visibleTaskIndices],
        field: recordType!CMGT_Task.taskInstanceUuid,
        value: local!activeAfterTaskBranches_thisTask[recordType!CMGT_TaskBranch.afterTaskInstanceUuid]
      )
    ),
    /*For the selected task in the middle pane*/
    local!selectableAfterTasks: rule!CMGT_UTIL_Filter(
      records: ri!availableTasks,
      field: recordType!CMGT_Task.taskInstanceUuid,
      value: {
        local!afterTasks[recordType!CMGT_Task.taskInstanceUuid]
      },
      filterType: "REJECT"
    ),
    local!selectableAfterTasks_thisDropdown: rule!CMGT_UTIL_sortList(
      records: rule!CMGT_UTIL_RejectBlank(
        a!flatten(
          {
            rule!CMGT_UTIL_Filter(
              records: local!afterTasks,
              field: recordType!CMGT_Task.taskInstanceUuid,
              value: rule!CMGT_UTIL_Filter(
                records: fv!item.afterTaskBranches,
                field: recordType!CMGT_TaskBranch.isActive,
                value: false,
                filterType: "REJECT"
              )[recordType!CMGT_TaskBranch.afterTaskInstanceUuid]
            ),
            local!selectableAfterTasks
          }
        )
      ),
      field: recordType!CMGT_Task.sortOrder,
      ascending: true
    ),
    local!selectedAfterTasks_thisTask: fv!item.afterTaskBranches[recordType!CMGT_TaskBranch.afterTaskInstanceUuid],
    local!selectedAfterTasks_thisTaskRecord: rule!CMGT_UTIL_Filter(
      records: ri!case[recordType!CMGT_Case.tasks],
      field: recordType!CMGT_Task.taskInstanceUuid,
      value: local!selectedAfterTasks_thisTask
    ),
    local!removedAfterTasks_thisTask: null,
    local!removedAfterTasks_thisTaskRecords,
    local!newAfterTasks_thisTask: null,
    local!newAfterTasks_thisTaskRecords,
    local!readdedAfterTaskBranches_thisTask,
    /*The required is not valid if the task is connected to start task or there is a
                    loop backs in the workflow
                    */
    local!isRequirednessValid: if(
      or(
        local!selectedAfterTasks_thisTaskRecord[recordType!CMGT_Task.startsByDefault],
        rule!CMGT_UTIL_IsNotBlank(
          rule!CMGT_UTIL_Filter(
            records: ri!previousTasks,
            field: recordType!CMGT_Task.taskInstanceUuid,
            value: local!selectedAfterTasks_thisTask
          )
        ),
        ri!taskBehaviorTypeId = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION
      ),
      false,
      true
    ),
    local!thisMap: fv!item,
    /*Dependents*/
    {
      /*Dropdown "labels"*/
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"SYSTEM_SYSRULES_richTextDisplayField"(
                labelPosition: "COLLAPSED",
                value: {
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: #"urn:appian:translation-string:v1:366b4e49-f096-489e-8cf8-16ac4860872d",
                    color: ri!brandingMap.SecondaryTextColor
                  )
                }
              )
            },
            width: "10X",
            showWhen: ri!taskBehaviorTypeId = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_DECISION
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"SYSTEM_SYSRULES_richTextDisplayField"(
                labelPosition: "COLLAPSED",
                value: {
                  #"SYSTEM_SYSRULES_richTextItem_v1"(
                    text: a!defaultValue(
                      ri!dropdownLabel,
                      #"urn:appian:translation-string:v1:b4ee65f7-13f4-417f-bd89-6fe9fd26151a"
                    ),
                    color: if(
                      a!isNullOrEmpty(ri!dropdownLabel),
                      ri!brandingMap.SecondaryTextColor,
                      ri!brandingMap.StandardTextColor
                    ),
                    style: if(
                      a!isNullOrEmpty(ri!dropdownLabel),
                      "PLAIN",
                      "STRONG"
                    )
                  )
                }
              )
            },
            width: "10X"
          ),
          #"SYSTEM_SYSRULES_columnLayout"(contents: {}, width: "1X")
        },
        alignVertical: "MIDDLE",
        marginAbove: "NONE",
        spacing: "DENSE",
        stackWhen: "NEVER",
        marginBelow: "EVEN_LESS"
      ),
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: #"SYSTEM_SYSRULES_dropdownField_v1"(
              choiceLabels: ri!taskResponseOptions[recordType!CMGT_TaskResponseOption.label],
              choiceValues: ri!taskResponseOptions[recordType!CMGT_TaskResponseOption.taskResponseOptionUuid],
              label: #"urn:appian:translation-string:v1:366b4e49-f096-489e-8cf8-16ac4860872d",
              labelPosition: "COLLAPSED",
              placeholder: #"urn:appian:translation-string:v1:91700f11-b292-41fb-add4-c0a73f8eff53",
              value: fv!item.taskResponseOptionUUID,
              saveInto: {
                fv!item.taskResponseOptionUUID,
                a!save(local!isRequirednessValid, true),
                a!save(
                  fv!item.afterTaskBranches,
                  rule!CMGT_UTIL_Filter(
                    records: a!flatten(
                      {
                        /* Set isActive=false for the removed afterTasks */
                        #"SYSTEM_SYSRULES_forEach"(
                          items: fv!item.afterTaskBranches,
                          expression: if(
                            a!isNullOrEmpty(
                              fv!item[recordType!CMGT_TaskBranch.taskBranchId]
                            ),
                            #"urn:appian:function:v1:a:update"(
                              fv!item,
                              {
                                recordType!CMGT_TaskBranch.afterTaskInstanceUuid,
                                recordType!CMGT_TaskBranch.isRequired,
                                recordType!CMGT_TaskBranch.isActive
                              },
                              { null, true, null }
                            ),
                            #"urn:appian:function:v1:a:update"(
                              fv!item,
                              recordType!CMGT_TaskBranch.isActive,
                              false
                            )
                          )
                        )
                      }
                    ),
                    fieldValuePairs: {
                      a!map(
                        field: recordType!CMGT_TaskBranch.isActive,
                        value: false()
                      ),
                      a!map(
                        field: recordType!CMGT_TaskBranch.taskBranchId,
                        value: null
                      )
                    },
                    filterType: "REJECT"
                  )
                )
              },
              accessibilityText: #"urn:appian:translation-string:v1:3597d5bb-0e51-4495-99b5-761793e3ad65"(
                #"urn:appian:translation-variable:v1:0999d4d2-c1db-4fd3-a6d1-35a4fa1a0019": fv!index
              )
            ),
            width: "10X",
            showWhen: ri!taskBehaviorTypeId = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_DECISION
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"SYSTEM_SYSRULES_multipleDropdownField_v1"(
                choiceLabels: local!selectableAfterTasks_thisDropdown[recordType!CMGT_Task.title],
                choiceValues: local!selectableAfterTasks_thisDropdown[recordType!CMGT_Task.taskInstanceUuid],
                label: a!defaultValue(
                  ri!dropdownLabel,
                  #"urn:appian:translation-string:v1:b4ee65f7-13f4-417f-bd89-6fe9fd26151a"
                ),
                labelPosition: "COLLAPSED",
                placeholder: #"urn:appian:translation-string:v1:c9dcd0d5-2e16-4d10-b544-3e3445922c79",
                value: rule!CMGT_UTIL_RejectBlank(
                  index(
                    rule!CMGT_UTIL_Filter(
                      records: fv!item.afterTaskBranches,
                      field: recordType!CMGT_TaskBranch.isActive,
                      value: { true(), null }
                    ),
                    recordType!CMGT_TaskBranch.afterTaskInstanceUuid,
                    {}
                  )
                ),
                saveInto: {
                  a!save(
                    local!selectedAfterTasks_thisTask,
                    save!value
                  ),
                  a!save(
                    local!selectedAfterTasks_thisTaskRecord,
                    rule!CMGT_UTIL_Filter(
                      records: ri!case[recordType!CMGT_Case.tasks],
                      field: recordType!CMGT_Task.taskInstanceUuid,
                      value: local!selectedAfterTasks_thisTask
                    )
                  ),
                  a!save(
                    local!removedAfterTasks_thisTask,
                    rule!CMGT_UTIL_Filter(
                      records: fv!item.afterTaskBranches,
                      field: recordType!CMGT_TaskBranch.afterTaskInstanceUuid,
                      value: { save!value, null },
                      filterType: "REJECT"
                    )[recordType!CMGT_TaskBranch.afterTaskInstanceUuid]
                  ),
                  a!save(
                    local!removedAfterTasks_thisTaskRecords,
                    rule!CMGT_UTIL_Filter(
                      records: ri!case[recordType!CMGT_Case.tasks],
                      field: recordType!CMGT_Task.taskInstanceUuid,
                      value: local!removedAfterTasks_thisTask
                    )
                  ),
                  a!save(
                    local!newAfterTasks_thisTask,
                    difference(
                      save!value,
                      touniformstring(
                        fv!item.afterTaskBranches[recordType!CMGT_TaskBranch.afterTaskInstanceUuid]
                      )
                    )
                  ),
                  a!save(
                    local!newAfterTasks_thisTaskRecords,
                    rule!CMGT_UTIL_Filter(
                      records: ri!case[recordType!CMGT_Case.tasks],
                      field: recordType!CMGT_Task.taskInstanceUuid,
                      value: local!newAfterTasks_thisTask
                    )
                  ),
                  a!save(
                    local!readdedAfterTaskBranches_thisTask,
                    rule!CMGT_UTIL_Filter(
                      records: fv!item.afterTaskBranches,
                      fieldValuePairs: {
                        a!map(
                          field: recordType!CMGT_TaskBranch.isActive,
                          value: false
                        ),
                        a!map(
                          field: recordType!CMGT_TaskBranch.afterTaskInstanceUuid,
                          value: save!value
                        )
                      }
                    )
                  ),
                  a!save(
                    local!isRequirednessValid,
                    if(
                      or(
                        local!selectedAfterTasks_thisTaskRecord[recordType!CMGT_Task.startsByDefault],
                        rule!CMGT_UTIL_IsNotBlank(
                          rule!CMGT_UTIL_Filter(
                            records: ri!previousTasks,
                            field: recordType!CMGT_Task.taskInstanceUuid,
                            value: local!selectedAfterTasks_thisTask
                          )
                        ),
                        ri!taskBehaviorTypeId = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION
                      ),
                      false,
                      true
                    )
                  ),
                  a!save(
                    fv!item.afterTaskBranches,
                    rule!CMGT_UTIL_Filter(
                      records: a!flatten(
                        {
                          /* Set isActive=false for the removed afterTasks */
                          #"SYSTEM_SYSRULES_forEach"(
                            items: rule!CMGT_UTIL_Filter(
                              records: fv!item.afterTaskBranches,
                              field: recordType!CMGT_TaskBranch.afterTaskInstanceUuid,
                              value: local!removedAfterTasks_thisTask
                            ),
                            expression: if(
                              a!isNullOrEmpty(
                                fv!item[recordType!CMGT_TaskBranch.taskBranchId]
                              ),
                              #"urn:appian:function:v1:a:update"(
                                fv!item,
                                {
                                  recordType!CMGT_TaskBranch.afterTaskInstanceUuid,
                                  recordType!CMGT_TaskBranch.isRequired,
                                  recordType!CMGT_TaskBranch.isActive
                                },
                                { null, true, null }
                              ),
                              #"urn:appian:function:v1:a:update"(
                                fv!item,
                                recordType!CMGT_TaskBranch.isActive,
                                false
                              )
                            )
                          ),
                          /* Construct new branches for new tasks */
                          #"SYSTEM_SYSRULES_forEach"(
                            items: local!newAfterTasks_thisTask,
                            expression: recordType!CMGT_TaskBranch(
                              recordType!CMGT_TaskBranch.beforeTaskInstanceUuid: local!thisMap.taskInstanceUUID,
                              recordType!CMGT_TaskBranch.afterTaskInstanceUuid: fv!item,
                              recordType!CMGT_TaskBranch.taskResponseOptionUuid: local!thisMap.taskResponseOptionUUID,
                              recordType!CMGT_TaskBranch.isActive: true(),
                              recordType!CMGT_TaskBranch.isCurrent: true(),
                              recordType!CMGT_TaskBranch.isRequired: local!isRequirednessValid,
                              /*local!thisMap.isRequired,*/
                              recordType!CMGT_TaskBranch.taskResponseOption: rule!CMGT_UTIL_Filter(
                                records: ri!taskResponseOptions,
                                field: recordType!CMGT_TaskResponseOption.taskResponseOptionUuid,
                                value: local!thisMap.taskResponseOptionUUID
                              )
                            )
                          ),
                          /* Retain exisitng afterTaskBranches */
                          rule!CMGT_UTIL_Filter(
                            records: fv!item.afterTaskBranches,
                            field: recordType!CMGT_TaskBranch.afterTaskInstanceUuid,
                            /* Not Retaining the re-added task as it is added separately below */
                            value: difference(
                              local!selectedAfterTasks_thisTask,
                              touniformstring(
                                index(
                                  local!readdedAfterTaskBranches_thisTask,
                                  recordType!CMGT_TaskBranch.afterTaskInstanceUuid,
                                  null
                                )
                              )
                            )
                          ),
                          /* Re Added task branches */
                          #"urn:appian:function:v1:a:update"(
                            local!readdedAfterTaskBranches_thisTask,
                            recordType!CMGT_TaskBranch.isActive,
                            true
                          )
                        }
                      ),
                      fieldValuePairs: {
                        a!map(
                          field: recordType!CMGT_TaskBranch.isActive,
                          value: false()
                        ),
                        a!map(
                          field: recordType!CMGT_TaskBranch.taskBranchId,
                          value: null
                        )
                      },
                      filterType: "REJECT"
                    )
                  ),
                  a!save(
                    {
                      fv!item.isRequired,
                      fv!item.afterTaskBranches[recordType!CMGT_TaskBranch.isRequired]
                    },
                    local!isRequirednessValid
                  ),
                  /* Reset startByDefault */
                  /*Set startByDefault = true when task is attached to start task else the value should be false*/
                  {
                    a!save(
                      ri!case[recordType!CMGT_Case.tasks],
                      if(
                        ri!isStartTask,
                        #"urn:appian:function:v1:a:update"(
                          data: #"urn:appian:function:v1:a:update"(
                            data: ri!case[recordType!CMGT_Case.tasks],
                            index: wherecontains(
                              touniformstring(local!removedAfterTasks_thisTask),
                              ri!case[recordType!CMGT_Case.tasks.taskInstanceUuid]
                            ),
                            value: #"urn:appian:function:v1:a:update"(
                              local!removedAfterTasks_thisTaskRecords,
                              recordType!CMGT_Task.startsByDefault,
                              false
                            )
                          ),
                          index: wherecontains(
                            touniformstring(local!newAfterTasks_thisTask),
                            ri!case[recordType!CMGT_Case.tasks.taskInstanceUuid]
                          ),
                          value: #"urn:appian:function:v1:a:update"(
                            local!newAfterTasks_thisTaskRecords,
                            recordType!CMGT_Task.startsByDefault,
                            true
                          )
                        ),
                        ri!case[recordType!CMGT_Case.tasks]
                      )
                    ),
                    a!save(
                      local!removedAfterTasks_thisTaskRecords,
                      null
                    )
                  }
                },
                disabled: ri!isDisabled,
                validations: a!match(
                  value: index(
                    rule!CMGT_UTIL_Filter(
                      records: ri!case[recordType!CMGT_Case.tasks],
                      field: recordType!CMGT_Task.taskInstanceUuid,
                      value: intersection(
                        ri!uniqueAfterTaskInstanceUUIDsInViolation,
                        fv!item.afterTaskBranches[recordType!CMGT_TaskBranch.afterTaskInstanceUuid]
                      )
                    ),
                    recordType!CMGT_Task.title,
                    {}
                  ),
                  whenTrue: rule!CMGT_UTIL_IsBlank(fv!value),
                  then: {},
                  whenTrue: length(fv!value) > 1,
                  then: #"urn:appian:translation-string:v1:8ad55d3b-6a21-4a0b-8d44-75cac7217ced"(
                    #"urn:appian:translation-variable:v1:ed415ae5-b6f9-4eac-a9b2-5b0741279d6a": joinarray(fv!value, ", ")
                  ),
                  default: #"urn:appian:translation-string:v1:705790e9-b65d-43e4-81a4-f7f531ab91b4"(
                    #"urn:appian:translation-variable:v1:2d1bd79c-13df-483b-bc6b-5389b6e25687": fv!value
                  )
                ),
                accessibilityText: {
                  #"urn:appian:translation-string:v1:3597d5bb-0e51-4495-99b5-761793e3ad65"(
                    #"urn:appian:translation-variable:v1:0999d4d2-c1db-4fd3-a6d1-35a4fa1a0019": fv!index
                  ),
                  if(
                    and(
                      ri!taskBehaviorTypeId = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION,
                      ri!isDefaultCondition <> true()
                    ),
                    #"urn:appian:translation-string:v1:15d88c07-b8c9-427f-b31a-0b006db8eecb"(
                      #"urn:appian:translation-variable:v1:a5ac7e4a-6448-4348-8ea7-00d692ec99a7": ri!displayIndex,
                      #"urn:appian:translation-variable:v1:1c43018a-cb5d-4f0b-a78e-0c326f400973": ri!countOfRules
                    ),
                    null()
                  )
                }
              )
            },
            width: "10X"
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: #"SYSTEM_SYSRULES_richTextDisplayField"(
              labelPosition: "COLLAPSED",
              value: #"SYSTEM_SYSRULES_richTextIcon"(
                icon: "times",
                altText: #"urn:appian:translation-string:v1:46d861d2-0082-4686-8b40-b7f6e8e0bed1"(
                  #"urn:appian:translation-variable:v1:ae4bfca0-5d96-495f-8965-8dc9059a97bc": fv!index
                ),
                link: #"SYSTEM_SYSRULES_dynamicLink"(
                  value: false(),
                  saveInto: {
                    fv!item.isActive,
                    fv!item.afterTaskBranches[recordType!CMGT_TaskBranch.isActive]
                  }
                ),
                linkStyle: "STANDALONE",
                color: ri!brandingMap.NegativeAccentColor,
                size: "MEDIUM"
              ),
              align: "CENTER"
            ),
            width: "1X",
            showWhen: and(
              not(
                and(
                  ri!taskBehaviorTypeId = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION,
                  ri!selectedTask[recordType!CMGT_Task.automationTaskAction.actionCode] = cons!CMGT_CFG_TXT_ACT_WORKFLOW_ROUTING
                )
              ),
              ri!isStartTask = false()
            )
          )
        },
        alignVertical: "MIDDLE",
        spacing: "DENSE",
        stackWhen: "NEVER",
        marginBelow: "EVEN_LESS"
      ),
      /*"Required for next task" selection*/
      #"SYSTEM_SYSRULES_sideBySideLayout"(
        items: {
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: #"SYSTEM_SYSRULES_checkboxField_v1"(
              choiceLabels: {
                #"urn:appian:translation-string:v1:00f79a9b-f640-4d17-947d-91cd0c30b346"
              },
              choiceValues: { true },
              labelPosition: "COLLAPSED",
              value: if(fv!item.isRequired, true, null),
              saveInto: {
                a!save(
                  {
                    fv!item.isRequired,
                    fv!item.afterTaskBranches[recordType!CMGT_TaskBranch.isRequired]
                  },
                  not(
                    a!defaultValue(value: fv!item.isRequired, default: false)
                  )
                )
              },
              disabled: or(
                not(local!isRequirednessValid),
                rule!CMGT_UTIL_IsBlank(
                  value: rule!CMGT_UTIL_RejectBlank(
                    index(
                      rule!CMGT_UTIL_Filter(
                        records: fv!item.afterTaskBranches,
                        field: recordType!CMGT_TaskBranch.isActive,
                        value: { true(), null }
                      ),
                      recordType!CMGT_TaskBranch.afterTaskInstanceUuid,
                      {}
                    )
                  )
                )
              ),
              accessibilityText: #"urn:appian:translation-string:v1:46d861d2-0082-4686-8b40-b7f6e8e0bed1"(
                #"urn:appian:translation-variable:v1:ae4bfca0-5d96-495f-8965-8dc9059a97bc": fv!index
              )
            ),
            width: "MINIMIZE"
          ),
          #"SYSTEM_SYSRULES_sideBySideItem"(
            item: #"SYSTEM_SYSRULES_richTextDisplayField"(
              labelPosition: "COLLAPSED",
              value: {
                #"SYSTEM_SYSRULES_richTextIcon"(
                  icon: "question-circle",
                  caption: if(
                    local!isRequirednessValid,
                    #"urn:appian:translation-string:v1:ce76a951-9b08-476e-9b54-f6da21399354",
                    #"urn:appian:translation-string:v1:7afd3cce-3e29-42a0-9b34-caa91ad8230f"
                  ),
                  color: ri!brandingMap.AccentColor
                )
              }
            ),
            width: "2X"
          )
        },
        alignVertical: "MIDDLE",
        showWhen: and(
          not(ri!isStartTask),
          not(
            ri!taskBehaviorTypeId = cons!CMGT_REFID_TASK_BEHAVIOR_TYPE_AUTOMATION
          )
        ),
        spacing: "DENSE",
        marginBelow: "EVEN_LESS"
      )
    }
  )
)
