#"SYSTEM_SYSRULES_forEach"(
  items: ri!conditionSets,
  expression: rule!CMGT_UTIL_SetRecordFields(
    record: fv!item,
    /*Always set these fields with each update*/
    alwaysOverwrite: {
      a!map(
        field: recordType!CMGT_ConditionSet.modifiedOn,
        value: now()
      ),
      a!map(
        field: recordType!CMGT_ConditionSet.modifiedBy,
        value: ri!initiator
      )
    },
    /*Only modify these fields if they are currently null*/
    neverOverwrite: {
      a!map(
        field: recordType!CMGT_ConditionSet.createdBy,
        value: ri!initiator
      ),
      a!map(
        field: recordType!CMGT_ConditionSet.createdOn,
        value: now()
      ),
      a!map(
        field: recordType!CMGT_ConditionSet.isActive,
        value: true
      )
    },
    childRelationships: {
      a!map(
        field: recordType!CMGT_ConditionSet.childConditionSet,
        value: rule!CMGT_ChildConditionSet_SetMetaData(
          conditionSets: fv!item[recordType!CMGT_ConditionSet.childConditionSet],
          initiator: ri!initiator
        )
      )
    }
  )
)
