sail_code:
if(
  and(
    ri!previousStatusOfSelectedTask <> ri!selectedTask[recordType!CMGT_Task.statusId],
    /*having a check to not recalculate if the status is being swithced between ready and inprogress*/
    if(
      ri!selectedTask[recordType!CMGT_Task.statusId] = cons!CMGT_REFID_STATUS_TASK_IN_PROGRESS,
      ri!previousStatusOfSelectedTask <> cons!CMGT_REFID_STATUS_TASK_READY,
      not(
        and(
          ri!previousStatusOfSelectedTask = cons!CMGT_REFID_STATUS_TASK_IN_PROGRESS,
          ri!selectedTask[recordType!CMGT_Task.statusId] = cons!CMGT_REFID_STATUS_TASK_READY
        )
      )
    ),
    or(
      ri!selectedTask[recordType!CMGT_Task.statusId] = cons!CMGT_REFID_STATUS_TASK_READY,
      ri!selectedTask[recordType!CMGT_Task.statusId] = cons!CMGT_REFID_STATUS_TASK_IN_PROGRESS
    )
  ),
  rule!CMGT_UTIL_CalculateDueDateBasedOnSLA(
    SLA: ri!selectedTask[recordType!CMGT_Task.sla],
    dateTime: now()
  ),
  ri!selectedTask[recordType!CMGT_Task.dueOn]
)
