sail_code:
#"SYSTEM_SYSRULES_forEach"(
  items: ri!taskCaseMap,
  expression: rule!CMGT_UTIL_SetRecordFields(
    record: fv!item,
    /*Always set these fields with each update*/
    alwaysOverwrite: {
      a!map(
        field: recordType!CMGT_TaskCaseMap.modifiedOn,
        value: now()
      ),
      a!map(
        field: recordType!CMGT_TaskCaseMap.modifiedBy,
        value: ri!initiator
      )
    },
    /*Only modify these fields if they are currently null*/
    neverOverwrite: {
      a!map(
        field: recordType!CMGT_TaskCaseMap.createdBy,
        value: ri!initiator
      ),
      a!map(
        field: recordType!CMGT_TaskCaseMap.createdOn,
        value: now()
      ),
      a!map(
        field: recordType!CMGT_TaskCaseMap.isActive,
        value: true
      )
    }
  )
)
