sail_code:
if(
  or(
    a!isNotNullOrEmpty(ri!assignedGroup),
    a!isNotNullOrEmpty(ri!assignedUser)
  ),
  #"urn:appian:function:v1:a:update"(
    recordType!CMGT_Role(
      recordType!CMGT_Role.groupId: ri!assignedGroup,
      recordType!CMGT_Role.roleTypeId: ri!assigneeTypeId,
      recordType!CMGT_Role.username: ri!assignedUser,
      recordType!CMGT_Role.caseId: ri!caseId
    ),
    recordType!CMGT_Role.caseAssignmentHistory,
    recordType!CMGT_CaseAssignmentHistory(
      recordType!CMGT_CaseAssignmentHistory.assignmentRuleId: ri!applicableAssignmentRule[recordType!CMGT_AssignmentRule.assignmentRuleId],
      recordType!CMGT_CaseAssignmentHistory.assignmentVersion: ri!applicableAssignmentRule[recordType!CMGT_AssignmentRule.assignmentVersion],
      recordType!CMGT_CaseAssignmentHistory.routingType: ri!applicableAssignmentRule[recordType!CMGT_AssignmentRule.routingType],
      recordType!CMGT_CaseAssignmentHistory.createdBy: ri!initiator,
      recordType!CMGT_CaseAssignmentHistory.createdOn: now()
    )
  ),
  {}
)
