#"SYSTEM_SYSRULES_forEach"(
  items: ri!emailTemplateAttachments,
  expression: rule!CMGT_UTIL_SetRecordFields(
    record: fv!item,
    /*Always set these fields with each update*/
    alwaysOverwrite: {
      a!map(
        field: recordType!CMGT_EmailTemplateAttachments.modifiedBy,
        value: ri!initiator
      ),
      a!map(
        field: recordType!CMGT_EmailTemplateAttachments.modifiedOn,
        value: now()
      )
    },
    /*Only modify these fields if they are currently null*/
    neverOverwrite: {
      a!map(
        field: recordType!CMGT_EmailTemplateAttachments.documentName,
        value: document(
          fv!item[recordType!CMGT_EmailTemplateAttachments.appianDocumentId],
          "name"
        )
      ),
      a!map(
        field: recordType!CMGT_EmailTemplateAttachments.extension,
        value: document(
          fv!item[recordType!CMGT_EmailTemplateAttachments.appianDocumentId],
          "extension"
        )
      ),
      a!map(
        field: recordType!CMGT_EmailTemplateAttachments.size,
        value: document(
          fv!item[recordType!CMGT_EmailTemplateAttachments.appianDocumentId],
          "size"
        )
      ),
      a!map(
        field: recordType!CMGT_EmailTemplateAttachments.createdBy,
        value: ri!initiator
      ),
      a!map(
        field: recordType!CMGT_EmailTemplateAttachments.createdOn,
        value: now()
      ),
      a!map(
        field: recordType!CMGT_EmailTemplateAttachments.isActive,
        value: true
      )
    }
  )
)
