sail_code:
a!localVariables(
  {
    /*Default Assignment Section*/
    if(
      /*Task block task is an "automated" task*/
      ri!isAutomated,
      /*default assignment is N/A for an automated task*/
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: #"urn:appian:translation-string:v1:7ed3fef7-c6cd-4516-a123-9883ccf20ac6"
      ),
      #"SYSTEM_SYSRULES_sectionLayout_v1"(
        contents: {
          #"SYSTEM_SYSRULES_richTextDisplayField"(
            value: {
              #"SYSTEM_SYSRULES_richTextIcon"(
                icon: "info-circle",
                color: ri!brandingMap.InfoAccentColor
              ),
              " ",
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: #"urn:appian:translation-string:v1:90602de9-6e83-4b53-8ebe-12c3ad2a7a49"
              )
            },
            showWhen: ri!disabled
          ),

          /*Default Assigned Group - Pick a group from CMGT Business Groups*/
          #"SYSTEM_SYSRULES_pickerFieldGroups"(
            label: #"urn:appian:translation-string:v1:64bf7660-8738-4162-81c8-166d776a7324",
            maxSelections: 1,
            groupFilter: cons!CMGT_GRP_BUSINESS_GROUPS,
            value: ri!selectedCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.defaultGroupAssignee],
            saveInto: {
              ri!selectedCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.defaultGroupAssignee],
              a!save(
                ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask],
                #"urn:appian:function:v1:a:update"(
                  ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask],
                  /*Index of the case type workflow task that we want to update in the ri!caseTypeWorkflowTasks array*/
                  wherecontains(
                    ri!selectedCaseTypeWorkflowTaskUuid,
                    ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask][recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid]
                  ),
                  /*The updated case type workflow task*/
                  #"urn:appian:function:v1:a:update"(
                    ri!selectedCaseTypeWorkflowTask,
                    recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.defaultGroupAssignee,
                    ri!selectedCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.defaultGroupAssignee]
                  )
                )
              )
            },
            showWhen: not(ri!disabled)
          )
        },
        marginBelow: "EVEN_LESS"
      )
    )
  }
)
