sail_code:
{
  /*Name*/
  #"SYSTEM_SYSRULES_textField"(
    label: #"urn:appian:translation-string:v1:e863390c-26e6-43ab-b6a2-9737045ffa59",
    value: ri!selectedCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.label],
    saveInto: {
      ri!selectedCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.label],
      a!save(
        ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask],
        #"urn:appian:function:v1:a:update"(
          ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask],
          /*Index of the case type workflow task that we want to update in the ri!caseTypeWorkflowTasks array*/
          wherecontains(
            ri!selectedCaseTypeWorkflowTaskUuid,
            ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask][recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid]
          ),
          /*The updated case type workflow task*/
          #"urn:appian:function:v1:a:update"(
            ri!selectedCaseTypeWorkflowTask,
            recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.label,
            a!defaultValue(
              ri!selectedCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.label],
              #"urn:appian:translation-string:v1:30830477-e9a2-4628-8fcd-40507aa24b3c"
            )
          )
        )
      )
    },
    characterLimit: cons!CMGT_INT_MAX_CHAR_LIMIT_MEDIUM,
    showCharacterCount: false,
    required: true,
    requiredMessage: #"urn:appian:translation-string:v1:4fab94dc-fcb1-427a-af9f-1239079b2897"(
      #"urn:appian:translation-variable:v1:3e31b6d5-df20-4d17-80a7-81f3c83ff370": #"urn:appian:translation-string:v1:0bae9b3f-5ddd-4c6a-a4cf-abe8702dac9e"
    )
  ),
  /*Description*/
  #"SYSTEM_SYSRULES_sectionLayout_v1"(
    label: #"urn:appian:translation-string:v1:0495bd73-7477-4bca-8918-2632c601a60b",
    labelSize: "EXTRA_SMALL",
    labelColor: "STANDARD",
    contents: rule!CMGT_UTIL_RichTextField(
      value: ri!selectedCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.description],
      readOnly: false(),
      maxSize: cons!CMGT_INT_MAX_CHAR_LIMIT_EXTRA_LARGE,
      label: #"urn:appian:translation-string:v1:0495bd73-7477-4bca-8918-2632c601a60b",
      labelPosition: "COLLAPSED",
      height: "SHORT",
      additionalSaves: {
        a!save(
          ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask],
          #"urn:appian:function:v1:a:update"(
            ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask],
            /*Index of the case type workflow task that we want to update in the ri!caseTypeWorkflowTasks array*/
            wherecontains(
              ri!selectedCaseTypeWorkflowTaskUuid,
              ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask][recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid]
            ),
            /*The updated case type workflow task*/
            #"urn:appian:function:v1:a:update"(
              ri!selectedCaseTypeWorkflowTask,
              recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.description,
              ri!selectedCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.description]
            )
          )
        )
      }
    ),
    isCollapsible: true,
    marginBelow: "LESS"
  ),
  /*Milestone*/
  #"SYSTEM_SYSRULES_sideBySideLayout"(
    items: {
      #"SYSTEM_SYSRULES_sideBySideItem"(
        item: #"SYSTEM_SYSRULES_dropdownField_v1"(
          choiceLabels: ri!activeMilestones[recordType!CMGT_WFL_CFG_ReferenceMilestone.label],
          choiceValues: ri!activeMilestones[recordType!CMGT_WFL_CFG_ReferenceMilestone.milestoneUuid],
          label: #"urn:appian:translation-string:v1:802692c1-f167-44d0-8058-196440edbb6a",
          placeholder: #"urn:appian:translation-string:v1:d6ec595a-cb00-4ca9-ad39-73fe092ca2eb",
          value: ri!selectedCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.referenceMilestone.milestoneUuid],
          saveInto: a!localVariables(
            local!milestone,
            {
              a!save(
                local!milestone,
                rule!CMGT_UTIL_Filter(
                  records: ri!activeMilestones,
                  field: recordType!CMGT_WFL_CFG_ReferenceMilestone.milestoneUuid,
                  value: save!value
                )
              ),
              a!save(
                ri!selectedCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.referenceMilestone],
                local!milestone
              ),
              a!save(
                ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask],
                #"urn:appian:function:v1:a:update"(
                  ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask],
                  wherecontains(
                    ri!selectedCaseTypeWorkflowTaskUuid,
                    ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask][recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid]
                  ),
                  #"urn:appian:function:v1:a:update"(
                    ri!selectedCaseTypeWorkflowTask,
                    recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.referenceMilestone,
                    ri!selectedCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.referenceMilestone]
                  )
                )
              )
            }
          )
        )
      ),
      #"SYSTEM_SYSRULES_sideBySideItem"(
        item: #"SYSTEM_SYSRULES_richTextDisplayField"(
          labelPosition: "COLLAPSED",
          value: {
            #"SYSTEM_SYSRULES_richTextIcon"(
              icon: "edit",
              caption: "Update Milestones",
              link: #"SYSTEM_SYSRULES_dynamicLink"(
                saveInto: {
                  a!save(
                    ri!currentWorkflowStep,
                    "UPDATE_REF_MILESTONES"
                  ),
                  a!save(ri!workingCaseType, ri!caseType)
                }
              ),
              linkStyle: "STANDALONE",
              size: "MEDIUM"
            )
          },
          marginBelow: "LESS"
        ),
        width: "MINIMIZE"
      )
    },
    alignVertical: "BOTTOM",
    stackWhen: "NEVER",
    marginBelow: "EVEN_LESS"
  )
}
