                            recordType!CMGT_WFL_CFG_TaskActions.caseTypeWorkflowTaskUuid: ri!thisWorkingCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid],
                            recordType!CMGT_WFL_CFG_TaskActions.taskActionUuid: rule!CMGT_UTIL_GenerateUuid_NonPlugin(),
                            recordType!CMGT_WFL_CFG_TaskActions.actionCode: save!value,
                            recordType!CMGT_WFL_CFG_TaskActions.isActive: true,

                          ),
                          #"urn:appian:function:v1:a:update"(
                            ri!thisWorkingCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTaskAction],
                            {
                              recordType!CMGT_WFL_CFG_TaskActions.actionCode,
                              recordType!CMGT_WFL_CFG_TaskActions.modifedBy,
                              recordType!CMGT_WFL_CFG_TaskActions.modifiedOn
                            },
                            { save!value, loggedInUser(), now() }
                          )
                        )
                      }
                    ),
                    a!save(
                      local!selectedAction,
                      rule!CMGT_UTIL_Filter(
                        records: ri!actions,
                        field: "actionCode",
                        value: save!value
                      )
                    ),
                    if(
                      or(
                        local!selectedAction.actionCode = "ASSIGN_CASE",
                        local!selectedAction.actionCode = "REASSIGN_CASE"
                      ),
                      a!save(
                        /*local!taskAction['recordType!{8b21790e-70b0-4a5e-a761-f1fcd071c536}CMGT_WFL_CFG_TaskActions.relationships.{de7a72ca-c484-48d9-974a-f834e5d302bd}associatedAssignmentRule'],*/
                        ri!thisWorkingCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTaskAction],
                        if(
                          rule!CMGT_UTIL_IsBlank(
                            ri!thisWorkingCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTaskAction]
                          ),
                          recordType!CMGT_CFG_AssignmentRules(
                            recordType!CMGT_CFG_AssignmentRules.taskActionUuid: ri!thisWorkingCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTaskAction.taskActionUuid]
                          ),
                          recordType!CMGT_CFG_AssignmentRules(
                            recordType!CMGT_CFG_AssignmentRules.assignmentRuleId: ri!thisWorkingCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTaskAction.associatedAssignmentRule.assignmentRuleId],
                            recordType!CMGT_CFG_AssignmentRules.taskActionUuid: ri!thisWorkingCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTaskAction.taskActionUuid],
                            recordType!CMGT_CFG_AssignmentRules.taskActionId: ri!thisWorkingCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTaskAction.taskActionId]
                          ),

                        )
                      ),
                      {}
                    )
                  },
                  placeholder: #"urn:appian:translation-string:v1:94dee9b4-fc55-4b1b-af21-a3f5656802be",
                  required: true,
                  validationGroup: ri!validationGroup
                )
              ),
              #"SYSTEM_SYSRULES_sideBySideItem"()
            }
          ),
              /*Using ri!thisWorkingCaseTypeWorkflowTask errors when passing into the configuration rule, so using array index instead */
          if(
            a!isNotNullOrEmpty(local!selectedAction),
            local!selectedAction[1].configurationRule(
              automationRuleTaskAction: ri!allWorkingCaseTypeWorkflowTasks[ri!thisWorkingTaskIndex][recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTaskAction],
              brandingMap: ri!brandingMap,
              validationGroup: ri!validationGroup
            ),
            {}
          )
        },
        showBorder: false,
        marginBelow: "STANDARD",
        shape: "SEMI_ROUNDED"
      ),
      /* Cancel/Save Buttons */
      #"SYSTEM_SYSRULES_ButtonArrayLayout"(
        buttons: {
          #"SYSTEM_SYSRULES_buttonWidget_v1"(
            label: #"urn:appian:translation-string:v1:3e56c457-a27a-49de-aa7d-e865696bf791",
            style: "SOLID",
            value: ri!thisWorkingCaseTypeWorkflowTask,
            saveInto: #"SYSTEM_SYSRULES_writeRecords_v2"(
              records: rule!CMGT_WFL_CaseTypeWorkflowTask_SetMetaData(
                caseTypeWorkflowTasks: ri!thisWorkingCaseTypeWorkflowTask,
                initiator: loggedInUser()
              ),
              onSuccess: {
                rule!CMGT_WFL_SetTrackAutomationRuleChanges(
                  isAddNew: ri!isAddNew,
                  trackAutomationRuleChanges: ri!trackAutomationRuleChanges,
                  recordsUpdated: fv!recordsUpdated,
                  allAutomationCaseTypeWorkflowTasks: ri!allAutomationCaseTypeWorkflowTasks
                ),
                a!save(
                  ri!automationCaseTypeWorkflowTask,
                  /*requerying for data instead of save fv!recordsUpdated to account for roles, if roles are not requeried it will mark old ones that were not removed as deleted*/
                  rule!CMGT_WFL_QR_GetCaseTypeWorkflowTask(
                    taskIntentionCodes: {
                      cons!CMGT_WFL_CASE_TYPE_TASK_INTENTION_CODE_TRIGGER
                    },
                    includeInactive: false,
                    fields: {
                      recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTaskAction,
                      recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTrigger,
                      /*to show the assignment rule selected*/
                      recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.automationTaskAction,
                      #"urn:appian:record-relationship:v1:d6ebee27-6e86-47e3-a9b8-71fdd0348854/b80565b5-ff65-4f64-8007-f2382adffd3f/de7a72ca-c484-48d9-974a-f834e5d302bd/69201ed0-4d96-49e9-805a-4bcf4d59ab9d"
                    },
                    additionalFilters: {
                      #"SYSTEM_SYSRULES_queryFilter"(
                        field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskId,
                        operator: "=",
                        value: fv!recordsUpdated[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskId][1]
                      )
                    }
                  )
                ),
                a!save(ri!ruleState.isReadOnly, true),
                a!save(ri!isAddNew, false),
                a!save(
                  ri!allWorkingCaseTypeWorkflowTasks,
                  rule!CMGT_UTIL_Filter(
                    records: ri!allWorkingCaseTypeWorkflowTasks,
                    field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid,
                    value: ri!thisWorkingCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid],
                    filterType: "REJECT"
                  )
                ),
                a!save(
                  ri!triggerRefresh,
                  now()
                )

              },
              onError: a!save(ri!error, fv!error),

            ),
            showWhen: not(ri!ruleState.isReadOnly),
            validate: true,
            validationGroup: ri!validationGroup,
            submit: false
          ),
          #"SYSTEM_SYSRULES_buttonWidget_v1"(
            label: #"urn:appian:translation-string:v1:651cf177-f30b-485b-900f-8aaf47805737",
            value: ri!automationCaseTypeWorkflowTask,
            saveInto: {
              ri!thisWorkingCaseTypeWorkflowTask,
              a!save(ri!ruleState.isReadOnly, true),
              a!save(ri!isAddNew, false),
              a!save(
                ri!allWorkingCaseTypeWorkflowTasks,
                rule!CMGT_UTIL_Filter(
                  records: ri!allWorkingCaseTypeWorkflowTasks,
                  field: recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid,
                  value: ri!thisWorkingCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid],
                  filterType: "REJECT"
                )
              ),
              if(
                ri!ruleState.isEdit,
                {},
                a!save(
                  ri!allAutomationCaseTypeWorkflowTasks,
                  remove(
                    ri!allAutomationCaseTypeWorkflowTasks,
                    ri!automationIndex
                  )
                )
              ),
              a!save(
                ri!triggerRefresh,
                now()
              )
            },
            showWhen: not(ri!ruleState.isReadOnly)
          ),

        }
      )
    }
  )
