sail_code:
#"SYSTEM_SYSRULES_forEach"(
  items: ri!taskAction,
  expression: rule!CMGT_UTIL_SetRecordFields(
    record: fv!item,
    /*Always set these fields with each update*/
    alwaysOverwrite: {
      a!map(
        field: recordType!CMGT_TaskActions.modifiedOn,
        value: now()
      ),
      a!map(
        field: recordType!CMGT_TaskActions.modifedBy,
        value: ri!initiator
      )
    },
    /*Only modify these fields if they are currently null*/
    neverOverwrite: {
      a!map(
        field: recordType!CMGT_TaskActions.createdBy,
        value: ri!initiator
      ),
      a!map(
        field: recordType!CMGT_TaskActions.createdOn,
        value: now()
      ),
      a!map(
        field: recordType!CMGT_TaskActions.isActive,
        value: true
      )
    },
    childRelationships:  {
      a!map(
        field: recordType!CMGT_TaskActions.associatedAssignmentRule,
        value: rule!CMGT_AssociatedAssignmentRule_SetMetaData(
          assignmentRules: fv!item[recordType!CMGT_TaskActions.associatedAssignmentRule],
          initiator: ri!initiator
        )
      ),

    }
  )
)
