sail_code:
a!localVariables(
  local!width: if(
    rule!CMGT_UTIL_IsMobileScreenSize(),
    "FILL",
    "MINIMIZE"
  ),
  #"SYSTEM_SYSRULES_wizardLayout"(
    titleBar: rule!CMGT_PortalInterface_BillboardLayout(
      title: #"urn:appian:translation-string:v1:dccad5ee-f64c-4507-9f7a-0a57b8155d3a",
      description: #"urn:appian:translation-string:v1:9e396e89-7bc5-4445-a3cb-5c6167da3fcc",
      showHeader: true(),
      brandingMap: ri!brandingMap,
      isWide: ri!isWide
    ),
    steps: {
      #"SYSTEM_SYSRULES_wizardStep"(
        label: #"urn:appian:translation-string:v1:b4ac67c8-7796-4541-8f72-ad5d5818daf9",
        contents: {
          #"SYSTEM_SYSRULES_sectionLayout_v1"(
            contents: #"SYSTEM_SYSRULES_richTextDisplayField"(
              value: {
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: #"urn:appian:translation-string:v1:7b3f115a-d6ac-4da4-87a8-5c5c737156da",
                  style: "STRONG",
                  size: "STANDARD"
                ),
                char(
                  32
                ),
                #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: "*",
                  style: "STRONG",
                  size: "STANDARD",
                  color: ri!brandingMap.AccentColor
                )
              }
            ),
            marginAbove: "NONE",
            marginBelow: "NONE"
          ),
          rule!CMGT_Portal_CaseTypes(
            case: ri!case,
            allCaseTypes: ri!allCaseTypes,
            pagingInfo: ri!pagingInfo,
            brandingMap: ri!brandingMap
          )
        },
        disableNextButton: if(
          a!isNullOrEmpty(
            ri!case[recordType!CMGT_Case.caseType]
          ),
          true,
          false
        )
      )/*Portal Intake Form*/
      ,
      #"urn:appian:control-panel-interface-type:v1:1b82dc2b-d4df-44ed-ac7c-3cce3b4ee0e0/6322c57d-c4ee-4ab5-b868-c48368ca23bc"(
        record: ri!case,
        controlPanelRecords: ri!data
      ),

    },
    primaryButtons: {
      #"SYSTEM_SYSRULES_buttonWidget_v1"(
        label: #"urn:appian:translation-string:v1:d3a45143-77d7-41aa-99e1-e562b4efd5ea",
        /*The Portal is intended to use recaptcha, however this isn't necessary.
      On execution in a Portal, it runs the dynamic verifyRecaptcha rule below, with more information.*/
        recaptchaSaveInto: rule!CMGT_UTIL_VerifyRecaptcha_SaveLogic(
          onSuccess: {
            #"SYSTEM_SYSRULES_submitUploadedFiles"(
              documents: ri!case[recordType!CMGT_Case.documents.appianDocId]
            ),
            #"SYSTEM_SYSRULES_startProcess3"(
              processModel: cons!CMGT_PM_CREATE_CASE,
              processParameters: {
                case: ri!case,
                data: ri!data
              },
              onSuccess: {
                a!save(
                  ri!caseSubmissionSuccessful,
                  true
                )
              }
            )
          },
          onError: a!save(
            ri!caseSubmissionSuccessful,
            false
          )
        ),
        submit: false,
        style: "SOLID",
        showWhen: fv!isLastStep,
        validate: true,
        width: local!width
      )
    },
    contentsWidth: "FULL",
    showStepHeadings: true,
    showButtonDivider: true(),
    isButtonFooterFixed: true(),
    focusOnFirstInput: true(),
    showTitleBarDivider: false(),
    isTitleBarFixed: false(),
    style: "DOT_VERTICAL"
  )
)
