sail_code:
#"SYSTEM_SYSRULES_sectionLayout_v1"(
  contents: {
    #"SYSTEM_SYSRULES_cardGroupLayout"(
      spacing: "STANDARD",
      marginAbove: "LESS",
      marginBelow: "EVEN_LESS",
      cards: {
        #"SYSTEM_SYSRULES_forEach"(
          items: ri!allCaseTypes.data,
          expression: #"SYSTEM_SYSRULES_cardLayout"(
            contents: {
              #"SYSTEM_SYSRULES_sideBySideLayout"(
                items: {
                  #"SYSTEM_SYSRULES_sideBySideItem"(
                    item: #"SYSTEM_SYSRULES_stampField"(
                      icon: fv!item[recordType!CMGT_CFG_CaseType.icon],
                      size: "TINY",
                      contentColor: ri!brandingMap.WhiteColor,
                      backgroundColor: fv!item[recordType!CMGT_CFG_CaseType.color],
                      shape: ri!brandingMap.StandardStampShape,

                    ),
                    width: "MINIMIZE"
                  ),
                  #"SYSTEM_SYSRULES_sideBySideItem"(
                    item: #"SYSTEM_SYSRULES_richTextDisplayField"(
                      labelPosition: "COLLAPSED",
                      value: {
                        #"SYSTEM_SYSRULES_richTextItem_v1"(
                          text: fv!item[recordType!CMGT_CFG_CaseType.name],
                          style: "STRONG"
                        ),
                        if(
                          a!isNotNullOrEmpty(
                            fv!item[recordType!CMGT_CFG_CaseType.parentCaseType.name]
                          ),
                          {
                            char(10),
                            #"SYSTEM_SYSRULES_richTextItem_v1"(
                              text: fv!item[recordType!CMGT_CFG_CaseType.parentCaseType.name],
                              color: ri!brandingMap.SecondaryTextColor,
                              size: "SMALL",

                            )
                          },
                          {}
                        )
                      },
                      marginAbove: "STANDARD",
                      marginBelow: "LESS"
                    ),

                  )
                },
                alignVertical: "MIDDLE",
                marginBelow: "NONE"
              ),
              #"SYSTEM_SYSRULES_richTextDisplayField"(
                value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                  text: fv!item[recordType!CMGT_CFG_CaseType.description],
                  size: "SMALL"
                )
              )
            },
            height: "SHORT",
            link: #"SYSTEM_SYSRULES_dynamicLink"(
              saveInto: {
                a!save(
                  ri!case,
                  rule!CMGT_Case_RecordAction_Create_InitializeCase(
                    source: cons!CMGT_TXT_SOURCE_PORTAL,
                    caseType: fv!item
                  )
                ),
                a!save(
                  ri!case[recordType!CMGT_Case.friendlyId],
                  rule!CMGT_Case_GenerateFriendlyIdentifier_Logic(case: ri!case)
                )
              }
            ),
            style: if(
              ri!case[recordType!CMGT_Case.caseTypeUuid] = fv!item[recordType!CMGT_CFG_CaseType.uuid],
              ri!brandingMap.SelectedCardColor,
              ri!brandingMap.CardBackgroundColor
            ),
            shape: ri!brandingMap.CardShape,
            padding: "LESS",
            marginBelow: "STANDARD",
            borderColor: if(
              ri!case[recordType!CMGT_Case.caseTypeUuid] = fv!item[recordType!CMGT_CFG_CaseType.uuid],
              ri!brandingMap.SelectedCardBorderColor,
              null
            ),
            showBorder: ri!brandingMap.CardShowBorder,
            showShadow: ri!brandingMap.CardShowShadow,
            decorativeBarPosition: "TOP",
            decorativeBarColor: fv!item[recordType!CMGT_CFG_CaseType.color]
          )
        )
      },
      cardWidth: "NARROW_PLUS",
      fillContainer: false,
      cardHeight: "SHORT"
    ),
    if(
      ri!allCaseTypes.totalCount > cons!CMGT_PORTAL_INT_BATCH_SIZE_MEDIUM_PLUS,
      rule!CMGT_UTIL_CustomPaging(
        pagingInfo: ri!pagingInfo,
        totalCount: ri!allCaseTypes.totalCount,
        brandingMap: ri!brandingMap
      ),
      {}
    )
  }
)
