sail_code:
#"SYSTEM_SYSRULES_billboardLayout_v1"(
  backgroundMedia: #"SYSTEM_SYSRULES_documentImage"(
    document: a!defaultValue(
      ri!headerBackgroundImage,
      ri!brandingMap.HeaderBannerImage
    )
  ),
  backgroundMediaPositionVertical: "TOP",
  height: a!defaultValue(ri!height, "SHORT_PLUS"),
  overlay: #"SYSTEM_SYSRULES_fullOverlay"(
    style: "NONE",
    contents: {
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {},
            width: "EXTRA_NARROW",
            showWhen: ri!isWide
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"SYSTEM_SYSRULES_columnsLayout"(
                columns: {
                  #"SYSTEM_SYSRULES_columnLayout"(
                    contents: {
                      #"SYSTEM_SYSRULES_cardLayout"(
                        contents: #"SYSTEM_SYSRULES_sectionLayout_v1"(
                          label: ri!title,
                          labelSize: "LARGE",
                          labelHeadingTag: "H1",
                          labelColor: ri!brandingMap.WhiteColor,
                          contents: {
                            #"SYSTEM_SYSRULES_richTextDisplayField"(
                              label: rule!CMGT_UTIL_ReturnLabelGivenAutomationTestingSwitch(testingLabel: "Description"),
                              labelPosition: "COLLAPSED",
                              value: {
                                #"SYSTEM_SYSRULES_richTextItem_v1"(
                                  text: { ri!description },
                                  color: ri!brandingMap.WhiteColor,
                                  size: "MEDIUM"
                                )
                              },
                              showWhen: a!isNotNullOrEmpty(ri!description)
                            ),
                            #"SYSTEM_SYSRULES_richTextDisplayField"(
                              label: rule!CMGT_UTIL_ReturnLabelGivenAutomationTestingSwitch(testingLabel: "Description2"),
                              labelPosition: "COLLAPSED",
                              value: {
                                #"SYSTEM_SYSRULES_richTextItem_v1"(
                                  text: { ri!secondaryDescription },
                                  color: ri!brandingMap.WhiteColor,
                                  size: "MEDIUM"
                                )
                              },
                              showWhen: a!isNotNullOrEmpty(ri!secondaryDescription)
                            )
                          },
                          marginBelow: "NONE",
                          marginAbove: "EVEN_MORE"
                        ),
                        showBorder: false,
                        style: "TRANSPARENT",
                        padding: if(ri!isWide, "NONE", "LESS")
                      )
                    },
                    width: "2X"
                  ),
                  #"SYSTEM_SYSRULES_columnLayout"(
                    contents: {
                      #"SYSTEM_SYSRULES_imageField_v1"(
                        images: {
                          #"SYSTEM_SYSRULES_documentImage"(document: ri!headerImage)
                        },
                        size:if(
                          #"SYSTEM_SYSRULES_isPageWidth"("TABLET_LANDSCAPE"),
                          "FIT",
                          "MEDIUM_PLUS"
                        ),
                        isThumbnail: false,
                        style: "STANDARD",
                        align: "CENTER",
                        marginAbove: "EVEN_LESS",
                        marginBelow: "EVEN_MORE"
                      )
                    },
                    showWhen: and(
                      a!isNotNullOrEmpty(ri!headerImage),
                      ri!isWide = true
                    )
                  )
                },
                marginBelow: "MORE",
                marginAbove: "NONE"
              )
            },
            width: "10X"
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {},
            width: "6X",
            showWhen: and(
              #"SYSTEM_SYSRULES_isPageWidth"(pageWidths: { "DESKTOP", "DESKTOP_WIDE" }),
              a!isNullOrEmpty(ri!headerImage)
            )
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {},
            width: "EXTRA_NARROW",
            showWhen: ri!isWide
          )
        },
        marginAbove: "STANDARD",
        marginBelow: "NONE"
      )
    }
  ),
  showWhen: ri!showHeader,
  marginBelow: "NONE",
  marginAbove: "NONE"
)
