sail_code:
#"SYSTEM_SYSRULES_cardLayout"(
  contents: {
    #"SYSTEM_SYSRULES_sideBySideLayout"(
      items: {
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: #"SYSTEM_SYSRULES_richTextDisplayField"(
            labelPosition: "COLLAPSED",
            value: {
              #"SYSTEM_SYSRULES_richTextIcon"(
                icon: a!defaultValue(ri!alertIcon, "exclamation-circle"),
                color: a!defaultValue(ri!alertIconColor, "NEGATIVE"),
                size: "MEDIUM"
              )
            }
          ),
          width: "MINIMIZE"
        ),
        #"SYSTEM_SYSRULES_sideBySideItem"(
          item: #"SYSTEM_SYSRULES_richTextDisplayField"(
            labelPosition: "COLLAPSED",
            value: {
              " ",
              ri!warningLabel,
              #"SYSTEM_SYSRULES_richTextItem_v1"(
                text: " " & joinarray(ri!dependentNames, ", "),
                showWhen: a!isNotNullOrEmpty(ri!dependentNames),
                style: "STRONG"
              )
            }
          )
        )
      },
      alignVertical: "MIDDLE",
      spacing: "STANDARD"
    )
  },
  showWhen: ri!showWhen,
  style: a!defaultValue(ri!alertStyle, "ERROR"),
  padding: a!defaultValue(value: ri!padding, default: "LESS"),
  marginBelow: "STANDARD",
  showBorder: false,
  decorativeBarPosition: "START",
  decorativeBarColor: a!defaultValue(ri!alertIconColor, "NEGATIVE")
)
