sail_code:
{
  /*Email Template*/
  #"SYSTEM_SYSRULES_sideBySideLayout"(
    items: {
      #"SYSTEM_SYSRULES_sideBySideItem"(
        item: #"SYSTEM_SYSRULES_dropdownField_v1"(
          choiceLabels: ri!emailTemplates[recordType!CMGT_WFL_CFG_EmailTemplate.templateName],
          choiceValues: ri!emailTemplates[recordType!CMGT_WFL_CFG_EmailTemplate.emailTemplateUuid],
          label: #"urn:appian:translation-string:v1:9ee41a2b-9ebc-4f9a-8581-138e2e348ba3",
          placeholder: #"urn:appian:translation-string:v1:6deffc1a-e4be-4a1b-a899-02820e6ca2bd",
          value: ri!selectedCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.sendEmailCaseTypeWorkflowTask.emailTemplateUuid],
          saveInto: {
            ri!selectedCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.sendEmailCaseTypeWorkflowTask.emailTemplateUuid],
            a!save(
              ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask],
              #"urn:appian:function:v1:a:update"(
                ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask],
                /*Index of the case type workflow task that we want to update in the ri!caseTypeWorkflowTasks array*/
                wherecontains(
                  ri!selectedCaseTypeWorkflowTaskUuid,
                  ri!caseType[recordType!CMGT_CFG_CaseType.caseTypeWorkflowTask][recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.caseTypeWorkflowTaskUuid]
                ),
                /*The updated case type workflow task*/
                #"urn:appian:function:v1:a:update"(
                  ri!selectedCaseTypeWorkflowTask,
                  recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.sendEmailCaseTypeWorkflowTask.emailTemplateUuid,
                  ri!selectedCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.sendEmailCaseTypeWorkflowTask.emailTemplateUuid]
                )
              )
            )
          },
          required: true,
          requiredMessage: #"urn:appian:translation-string:v1:4fab94dc-fcb1-427a-af9f-1239079b2897"(
            #"urn:appian:translation-variable:v1:3e31b6d5-df20-4d17-80a7-81f3c83ff370": #"urn:appian:translation-string:v1:9ee41a2b-9ebc-4f9a-8581-138e2e348ba3"
          )
        ),
        width: "10X"
      ),
      #"SYSTEM_SYSRULES_sideBySideItem"(
        item: #"SYSTEM_SYSRULES_ButtonArrayLayout"(
          buttons: #"SYSTEM_SYSRULES_buttonWidget_v1"(
            label: #"urn:appian:translation-string:v1:8081c5e8-c2e7-4294-af43-28e7a98658e6",
            saveInto: a!save(ri!isPreview, true()),
            disabled: rule!CMGT_UTIL_IsBlank(
              value: ri!selectedCaseTypeWorkflowTask[recordType!CMGT_WFL_CFG_CaseTypeWorkflowTask.sendEmailCaseTypeWorkflowTask.emailTemplateUuid]
            )
          ),
          align: "START",
          marginBelow: "NONE",
          marginAbove: "LESS"
        ),
        width: "MINIMIZE"
      )
    },
    marginBelow: "NONE",
    marginAbove: "EVEN_LESS",
    alignVertical: "BOTTOM"
  ),
  /*Create Template*/
  #"SYSTEM_SYSRULES_ButtonArrayLayout"(
    buttons: #"SYSTEM_SYSRULES_buttonWidget_v1"(
      label: #"urn:appian:translation-string:v1:e2e5d9f2-a4d2-45e2-8011-e195eaeb07f7",
      style: "LINK",
      icon: "plus-circle",
      saveInto: a!save(
        ri!currentWorkflowStep,
        "CREATE_EMAIL_TEMPLATE"
      )
    ),
    align: "START",
    marginBelow: "LESS",
    marginAbove: "LESS"
  ),
  /*Recipients*/
  rule!CMGT_WFL_SendEmail_Recipients(
    brandingMap: ri!brandingMap,
    caseType: ri!caseType,
    selectedCaseTypeWorkflowTaskUuid: ri!selectedCaseTypeWorkflowTaskUuid,
    selectedCaseTypeWorkflowTask: ri!selectedCaseTypeWorkflowTask
  )
}
