sail_code:
if(
  /*Case task is an "automated" task*/
  ri!isAutomated,
  /*Assigned Group and Assignee is N/A for an automated task*/
  #"SYSTEM_SYSRULES_sideBySideLayout"(
    items: {
      #"SYSTEM_SYSRULES_sideBySideItem"(
        item: #"SYSTEM_SYSRULES_richTextDisplayField"(
          label: #"urn:appian:translation-string:v1:9ef20f7e-d182-48ed-8b39-7a5219d71816",
          labelPosition: "ABOVE",
          value: #"urn:appian:translation-string:v1:7ed3fef7-c6cd-4516-a123-9883ccf20ac6",
          marginAbove: "EVEN_LESS"
        )
      ),
      #"SYSTEM_SYSRULES_sideBySideItem"(
        item: #"SYSTEM_SYSRULES_richTextDisplayField"(
          label: #"urn:appian:translation-string:v1:e4b1d453-add2-4441-a242-a5c4952c6f9e",
          labelPosition: "ABOVE",
          value: #"urn:appian:translation-string:v1:7ed3fef7-c6cd-4516-a123-9883ccf20ac6",
          marginAbove: "EVEN_LESS"
        )
      )
    },
    marginAbove: "EVEN_LESS",
    marginBelow:"EVEN_LESS"
  ),
  if(
    ri!selectedTask[recordType!CMGT_Task.statusId]=cons!CMGT_REFID_STATUS_TASK_QUEUED,
    rule!CMGT_Case_ConfigureTask_Task_DefaultAssignmentSection(
      selectedTask: ri!selectedTask,
      isAutomated: ri!isAutomated,
      case: ri!case,
      selectedTaskInstanceUUID: ri!selectedTaskInstanceUUID,
      brandingMap: ri!brandingMap,
      disabled: ri!numOfAssociatedAutomationRules > 0,
    ),
  /*Picker field for Assigned Group and Assignee*/
  rule!CMGT_Role_Sub_ManageUi(
    roles: ri!case[recordType!CMGT_Case.tasks][rule!CMGT_UTIL_Filter(
      records: ri!case[recordType!CMGT_Case.tasks],
      field: recordType!CMGT_Task.taskInstanceUuid,
      value: ri!selectedTaskInstanceUUID,
      filterType: "MATCH_INDICES"
    )][1][recordType!CMGT_Task.roles],
    rolesToDelete: ri!rolesToDelete,
    taskId: ri!selectedTask[recordType!CMGT_Task.taskId],
    roleTypes: rule!CMGT_QR_GetRoleType(
      roleTypeId: {
        cons!CMGT_REFID_ROLE_TYPE_USER_ASSIGNEE,
        cons!CMGT_REFID_ROLE_TYPE_GROUP_ASSIGNEE
      }
    ),
    displayAsSideBySide: false()
  )
  )
)
