sail_code:
a!localVariables(
  local!sendEmailTaskSelection: rule!CMGT_UTIL_Filter(
    records: ri!sendEmailTaskSelectionMaps,
    field: "taskInstanceUuid",
    value: ri!selectedTask[recordType!CMGT_Task.taskInstanceUuid]
  ).sendEmailTaskSelection,
  local!selectedEmailTemplate,
  {
    if(
      and(
        rule!CMGT_UTIL_IsBlank(
          value: ri!selectedTask[recordType!CMGT_Task.taskId]
        ),
        index(
          ri!selectedTask,
          recordType!CMGT_Task.automationTaskAction.actionCode,
          null
        ) = cons!CMGT_CFG_TXT_ACT_SEND_EMAIL
      ),
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: rule!CMGT_Task_RecordAction_WorkflowEdit_CreateNewSendEmailTasks(
              currentNewTask: ri!selectedTask,
              isEmailTemplatePreview: ri!isEmailTemplatePreview,
              sendEmailTaskSelection: local!sendEmailTaskSelection,
              selectedEmailTemplate: local!selectedEmailTemplate,
              sendEmailTaskSelectionMaps: ri!sendEmailTaskSelectionMaps,
              case: ri!case
            ),

          )
        },
        marginBelow: "STANDARD"
      ),
      {
        /*Email Name*/
        #"SYSTEM_SYSRULES_columnsLayout"(
          columns: #"SYSTEM_SYSRULES_columnLayout"(
            contents: #"SYSTEM_SYSRULES_textField"(
              label: #"urn:appian:translation-string:v1:ead5fcb5-744c-454b-b451-b6958fc4adb8",
              value: ri!selectedTask[recordType!CMGT_Task.sendEmailTask.templateName],
              readOnly: true()
            )
          ),
          marginAbove: "EVEN_LESS"
        ),
        #"SYSTEM_SYSRULES_ButtonArrayLayout"(
          buttons: #"SYSTEM_SYSRULES_buttonWidget_v1"(
            label: #"urn:appian:translation-string:v1:8081c5e8-c2e7-4294-af43-28e7a98658e6",
            saveInto: a!save(ri!isEmailTemplatePreview, true)
          ),
          align: "START",
          marginAbove: "NONE",
          marginBelow: "STANDARD"
        )
      }
    ),
    /*Recipients*/
    rule!CMGT_Task_SendEmail_Recipients(
      brandingMap: ri!brandingMap,
      case: ri!case,
      selectedTask: ri!selectedTask,
      selectedTaskUuid: ri!selectedTaskInstanceUUID,
      caseType: ri!caseType
    )
  }
)
